import React, { useState, useEffect } from 'react';
import { getAllSongsByUserId } from '../Api/SongApi';

const ListSong = () => {
    const [songs, setSongs] = useState([]);
    const user = JSON.parse(localStorage.getItem('user') || '{}');

    useEffect(() => {
        if (!user || !user.Id) {
            console.error('User ID is missing');
            return;
        }

        // Directly set the state without using localStorage
        getAllSongsByUserId(user.Id)
            .then(fetchedSongs => {
                setSongs(fetchedSongs);
            })
            .catch(error => console.error('Error fetching songs:', error));
    }, [user.Id]);

    return (
        <div>
            <h2>Songs by User</h2>
            <ul>
                {songs.map((song, index) => (
                    <li key={song.SongId || index}> {/* Fallback to index if SongId is not available */}
                        <strong>Title:</strong> {song.title}
                        <div>
                            {song.imageData && (
                                <div>
                                    <p><strong>Image:</strong></p>
                                    <img src={`data:image/png;base64,${song.imageData}`} alt={song.title + ' cover art'} />
                                </div>
                            )}
                            {song.soundTrackData && (
                                <div>
                                    <p><strong>Soundtrack:</strong></p>
                                    <audio controls>
                                        <source src={`data:audio/mpeg;base64,${song.soundTrackData}`} type="audio/mpeg" />
                                        Your browser does not support the audio element.
                                    </audio>
                                </div>
                            )}
                        </div>
                    </li>
                ))}
            </ul>

        </div>
    );
};

export default ListSong;
