import React, { useState, useEffect, useRef } from "react";
import AudioControls from "./AudioControls";
import volumeIcon from '../Icon/icons8-sound-48.png';
// import "./styles.css";

/*
 * Read the blog post here:
 * https://letsbuildui.dev/articles/building-an-audio-player-with-react-hooks
 */
const AudioPlayer = ({ tracks }) => {
    // State
    const [trackIndex, setTrackIndex] = useState(0);
    const [trackProgress, setTrackProgress] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);

    // Destructure for conciseness
    const { title, artist, audioSrc,image } = tracks[trackIndex];

    // Refs
    const audioRef = useRef(new Audio(audioSrc));
    const intervalRef = useRef();
    const isReady = useRef(false);

    // Destructure for conciseness
    const { duration } = audioRef.current;

    const currentPercentage = duration
        ? `${(trackProgress / duration) * 100}%`
        : "0%";
    const trackStyling = `
    -webkit-gradient(linear, 0% 0%, 100% 0%, color-stop(${currentPercentage}, #fff), color-stop(${currentPercentage}, #777))
  `;

    const startTimer = () => {
        // Clear any timers already running
        clearInterval(intervalRef.current);

        intervalRef.current = setInterval(() => {
            if (audioRef.current.ended) {
                toNextTrack();
            } else {
                setTrackProgress(audioRef.current.currentTime);
            }
        }, [1000]);
    };

    const onScrub = (value) => {
        // Clear any timers already running
        clearInterval(intervalRef.current);
        audioRef.current.currentTime = value;
        setTrackProgress(audioRef.current.currentTime);
    };

    const onScrubEnd = () => {
        // If not already playing, start
        if (!isPlaying) {
            setIsPlaying(true);
        }
        startTimer();
    };

    const toPrevTrack = () => {
        if (trackIndex - 1 < 0) {
            setTrackIndex(tracks.length - 1);
        } else {
            setTrackIndex(trackIndex - 1);
        }
    };

    const toNextTrack = () => {
        if (trackIndex < tracks.length - 1) {
            setTrackIndex(trackIndex + 1);
        } else {
            setTrackIndex(0);
        }
    };

    useEffect(() => {
        if (isPlaying) {
            audioRef.current.play();
            startTimer();
        } else {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    // Handles cleanup and setup when changing tracks
    useEffect(() => {
        audioRef.current.pause();

        audioRef.current = new Audio(audioSrc);
        setTrackProgress(audioRef.current.currentTime);

        if (isReady.current) {
            audioRef.current.play();
            setIsPlaying(true);
            startTimer();
        } else {
            // Set the isReady ref as true for the next pass
            isReady.current = true;
        }
    }, [trackIndex]);

    useEffect(() => {
        // Pause and clean up on unmount
        return () => {
            audioRef.current.pause();
            clearInterval(intervalRef.current);
        };
    }, []);


    const playerContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        maxWidth: '920px', // Set a max-width for the player
        margin: '0 auto', // Center the player
        padding: '0 20px', // Add padding on the sides
        border: '2px solid #ccc', // Add border to left section

    };

    const leftSectionStyle = {
        display: 'flex',
        alignItems: 'center',
        margin: '10px',
        border: '2px solid #ccc', // Add border to left section

    };
    const centerSectionStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        border: '2px solid #ccc', // Add border to left section

    };

    const rightSectionStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        border: '2px solid #ccc', // Add border to left section
        marginRight:'10px'
    };

    const progressBarStyle = {
        width: '100%', // Allow the progress bar to fill the width of the center section
        marginBottom: '10px', // Space between progress bar and control icons
        border: '2px solid #ccc', // Add border to left section

    };


    return (
        <div className="audio-player" style={playerContainerStyle}>
            <div style={leftSectionStyle}>
                <img
                    src={tracks[trackIndex].image}
                    alt={`Track artwork for ${tracks[trackIndex].title} by ${tracks[trackIndex].artist}`}
                    style={{ width: '120px', height: '120px', borderRadius: '120px' }}
                />
                <div>
                    <h2>{tracks[trackIndex].title}</h2>
                    <h3>{tracks[trackIndex].artist}</h3>
                </div>

            </div>

            <div style={centerSectionStyle}>
                <AudioControls
                    isPlaying={isPlaying}
                    onPrevClick={toPrevTrack}
                    onNextClick={toNextTrack}
                    onPlayPauseClick={setIsPlaying}
                />
                <input
                    type="range"
                    value={trackProgress}
                    step="1"
                    min="0"
                    max={duration || 0}
                    className="progress"
                    onChange={(e) => onScrub(e.target.value)}
                    onMouseUp={onScrubEnd}
                    onKeyUp={onScrubEnd}
                    style={progressBarStyle}
                />
            </div>

            <div style={rightSectionStyle}>
                <img
                    src={volumeIcon}
                    alt="Volume control"
                    style={{ width: '48px', height: '48px' }}
                />
            </div>
        </div>
    );
};

export default AudioPlayer;
