import React, { useState } from 'react';
import { register } from '../Api/api';

const Registration = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [gender, setGender] = useState('NonBinary'); // Default value for gender
    const [errorMessage, setErrorMessage] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (password !== confirmPassword) {
            setErrorMessage('The password and confirmation password do not match.');
            return;
        }

        const newUser = {
            email,
            password,
            confirmPassword,
            gender // Include the selected gender in the request
        };

        const response = await register(newUser);

        if (response.ok) {
            console.log('User registered successfully.');
        } else {
            const errorData = await response.json();
            setErrorMessage(errorData.message);
        }
    };

    return (
        <div className="center-container"> {/* Container for centering */}
            <div className="registration-container">
                <h2>Register</h2>
                {errorMessage && <div className="error-message">{errorMessage}</div>}
                <form onSubmit={handleSubmit} className="registration-form">
                    <div className="form-group">
                        <label>Email:</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    </div>
                    <div className="form-group">
                        <label>Password:</label>
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </div>
                    <div className="form-group">
                        <label>Confirm Password:</label>
                        <input type="password" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
                    </div>
                    <div className="form-group">
                        <label>Gender:</label>
                        <select value={gender} onChange={(e) => setGender(e.target.value)}>
                            <option value="Male">Male</option>
                            <option value="Female">Female</option>
                            <option value="PreferNotToSay">Prefer Not to Say</option>
                            <option value="Other">Other</option>
                            <option value="NonBinary">Non-Binary</option>
                        </select>
                    </div>
                    <button type="submit" className="registration-button">Register</button>
                </form>
            </div>
        </div>
    );
};

export default Registration;
