import React, { useState } from 'react';
import { addNewSong } from '../../Api/SongApi';
import { useNavigate } from 'react-router-dom';
import { getStorage, ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";

const AddPage = () => {
    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [genre, setGenre] = useState('');
    const [privacy, setPrivacy] = useState('public');
    const [isUploading, setIsUploading] = useState(false);
    const [soundtrackUrl, setSoundtrackUrl] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const navigate = useNavigate();

    const genreToIntMap = {
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    };

    const handleTitleChange = (event) => setTitle(event.target.value);
    const handleSoundtrackChange = (event) => setSoundtrackFile(event.target.files[0]);
    const handleImageChange = (event) => setImageFile(event.target.files[0]);
    const handleGenreChange = (event) => setGenre(event.target.value); // Adjusted to use value directly from event
    const handlePrivacyChange = (event) => setPrivacy(event.target.value);

    const uploadFilesAndGetUrls = async () => {
        setIsUploading(true);
        // Initialize Firebase Storage and create references for both files
        const storage = getStorage();
        const soundtrackRef = ref(storage, `SoundTrack/${soundtrackFile.name}`);
        const imageRef = ref(storage, `SoundTrackImage/${imageFile.name}`);

        // Use Promise.all to wait for both uploads to complete
        const [soundtrackSnapshot, imageSnapshot] = await Promise.all([
            uploadBytesResumable(soundtrackRef, soundtrackFile),
            uploadBytesResumable(imageRef, imageFile),
        ]);

        // Get the download URLs for both files
        const [soundtrackDownloadUrl, imageDownloadUrl] = await Promise.all([
            getDownloadURL(soundtrackSnapshot.ref),
            getDownloadURL(imageSnapshot.ref),
        ]);

        setSoundtrackUrl(soundtrackDownloadUrl);
        setImageUrl(imageDownloadUrl);
        setIsUploading(false);
    };

    const addSong = async (event) => {
        event.preventDefault();
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (!user || !user.Id) {
            console.error('User ID is missing');
            return;
        }
        await uploadFilesAndGetUrls();
        if (!isUploading && soundtrackUrl && imageUrl) {
            // const durationInSeconds = Math.floor(audio.duration); // Get duration in seconds
            // const minutes = Math.floor(durationInSeconds / 60);
            // const seconds = durationInSeconds % 60;
            // const formattedDuration = `${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`;
            const formData = new FormData();
            formData.append('userId', user.Id);
            formData.append('title', title);
            formData.append('AudioFileUrl', soundtrackUrl);
            formData.append('ImageFileUrl', imageUrl);
            formData.append('genreId', genreToIntMap[genre].toString());
            formData.append('privacy', privacy === "public" ? "true" : "false");
            // formData.append('duration', formattedDuration);

            for (let [key, value] of formData.entries()) {
                console.log(`${key}:`, value);
            }
            try {
                await addNewSong(formData);
                navigate('/ListSong');
                window.location.reload();
            } catch (error) {
                console.error('Error:', error);
            }
        }
    };
    const genreOptions = Object.keys(genreToIntMap).map(genreName => (
        <option key={genreName} value={genreName}>{genreName}</option>
    ));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add New Song</h2>
                <form onSubmit={addSong}>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Title:</label>
                        <input type="text" value={title} onChange={handleTitleChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Soundtrack File:</label>
                        <input type="file" onChange={handleSoundtrackChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Image File:</label>
                        <input type="file" onChange={handleImageChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Genre:</label>
                        <select value={genre} onChange={handleGenreChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="">Select Genre</option>
                            {genreOptions}
                        </select>
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Privacy:</label>
                        <select value={privacy} onChange={handlePrivacyChange} style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="public">Public</option>
                            <option value="private">Private</option>
                        </select>
                    </div>
                    <button type="submit" style={{ width: '100%', padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', cursor: 'pointer' }}>Add Song</button>
                </form>
            </div>
        </div>
    );
};

export default AddPage;
