import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, deleteSongById, getSongById } from '../../Api/SongApi';

const EditSong = () => {
    const { songId } = useParams();
    const [song, setSong] = useState({
        title: '',
        soundTrackFileName: '',
        imageFileName: '',
        // Initialize other attributes if needed
    });
    const [imageFile, setImageFile] = useState(null);
    const [soundTrackFile, setSoundTrackFile] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSong = async () => {
            try {
                const songData = await getSongById(songId);
                setSong(songData);
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId]);

    const handleImageChange = (e) => {
        setImageFile(e.target.files[0]);
    };

    const handleSoundTrackChange = (e) => {
        setSoundTrackFile(e.target.files[0]);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('title', song.title);
        if (imageFile) formData.append('image', imageFile);
        if (soundTrackFile) formData.append('soundTrack', soundTrackFile);

        try {
            await updateSongById(songId, formData);
            alert('Song updated successfully');
            navigate('/ListSong'); // Navigate back to the list of songs
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    const handleDelete = async () => {
        const confirmation = window.confirm('Are you sure you want to delete this song?');
        if (confirmation) {
            try {
                await deleteSongById(songId);
                alert('Song deleted successfully');
                navigate('/ListSong'); // Navigate back to the list of songs
            } catch (error) {
                console.error('Error deleting the song:', error);
            }
        }
    };

    return (
        <div className="edit-song-container">
            <h2>Edit Song</h2>
            <form onSubmit={handleSubmit} className="edit-song-form">
                <div className="image-preview" style={{ textAlign: 'center' }}>
                    <img
                        src={`data:image/png;base64,${song.imageData}`}
                        alt={`${song.title} cover art`}
                        style={{ width: '150px', height: '150px' }}
                    />
                    <input type="file" onChange={handleImageChange} />
                </div>
                <div className="soundtrack-preview" style={{ textAlign: 'center' }}>
                    {song.soundTrackFileName && (
                        <audio controls style={{ marginTop: '20px' }}>
                            <source src={`data:audio/mpeg;base64,${song.soundTrackData}`} type="audio/mpeg" />
                            Your browser does not support the audio element.
                        </audio>
                    )}
                    <input type="file" onChange={handleSoundTrackChange} />
                </div>
                <div className="form-controls" style={{ textAlign: 'center', marginTop: '20px' }}>
                    <button type="submit" className="save-btn">Save Changes</button>
                    <button type="button" onClick={handleDelete} className="delete-btn">Delete Song</button>
                </div>
            </form>
        </div>
    );
};

export default EditSong;
