import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../Cart/CartContext';
import { addOrder, createPayPalPayment } from '../../utils/Api/OrderApi';  // Assuming createPayPalPayment is implemented

const CheckoutPage = () => {
    const history = useNavigate();
    const { cartItems } = useCart();
    const [customerInfo, setCustomerInfo] = useState({
        name: '',
        email: '',
        phoneNumber: '',
        address: ''
    });
    const [orderDetails, setOrderDetails] = useState({
        orderPlaced: false,
        orderId: '',
        paypalUrl: ''  // Store PayPal URL here
    });

    const calculateTotalPrice = () => cartItems.reduce((total, item) => (total + item.product.price * item.quantity), 0);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setCustomerInfo(prev => ({ ...prev, [name]: value }));
    };

    const handlePlaceOrder = async () => {
        const totalPrice = calculateTotalPrice().toFixed(2); // Ensure formatting if needed
        const orderInfo = {
            userId: getUserIdFromLocalStorage(),
            cartItems: cartItems.map(item => ({
                productId: item.product.productId,
                quantity: item.quantity,
                size: item.size
            })),
            ...customerInfo,
            totalPrice
        };

        try {
            const orderResponse = await addOrder(orderInfo);
            if (orderResponse.orderId) {
                const paymentResponse = await createPayPalPayment(orderResponse.orderId, totalPrice);
                if (paymentResponse.paypalUrl) {
                    window.location.href = paymentResponse.paypalUrl; // Redirect user to PayPal
                } else {
                    console.error('PayPal URL not received:', paymentResponse.error);
                    history('/paymentFailure');
                }
            } else {
                throw new Error('Order creation failed');
            }
        } catch (error) {
            console.error('Error placing order:', error);
            history('/paymentFailure');
        }
    };


    const getUserIdFromLocalStorage = () => {
        const userData = localStorage.getItem('user');
        return userData ? JSON.parse(userData).Id : null;
    };

    return (
        <div>
            <h2>Checkout</h2>
            <form>
                {['name', 'email', 'phoneNumber', 'address'].map(field => (
                    <div key={field}>
                        <label>{field.charAt(0).toUpperCase() + field.slice(1)}:</label>
                        <input
                            type={field === 'email' ? 'email' : field === 'phoneNumber' ? 'tel' : 'text'}
                            value={customerInfo[field]}
                            onChange={handleInputChange}
                            name={field}
                        />
                    </div>
                ))}
            </form>
            <h2>Order Summary</h2>
            <div>
                {cartItems.map((item, index) => (
                    <div key={index}>
                        <p>Product: {item.product.name}</p>
                        <p>Size: {item.size}</p>
                        <p>Quantity: {item.quantity}</p>
                        <p>Total Price: {(item.product.price * item.quantity).toLocaleString()} USD</p>
                    </div>
                ))}
            </div>
            <p>Total Order Price: {calculateTotalPrice().toLocaleString()} USD</p>
            <button onClick={handlePlaceOrder}>Place Order</button>
        </div>
    );
};

export default CheckoutPage;
