import React, { useState, useEffect } from 'react';
import { getAllProducts } from '../../utils/Api/ProductApi';
import CartItem from '../Cart/CartItem';
import './ProductList.css';

const ProductList = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const productsData = await getAllProducts();
                console.log('product data: ',productsData)
                setProducts(productsData);
            } catch (error) {
                console.error('Error fetching products:', error);
            }
        };
        fetchProducts();
    }, []);

    const handleProductClick = (productId) => {
        console.log('Clicked product ID:', productId);
    };

    return (
        <div className="product-list">
            {products.map(product => (
                <div key={product.productId} className="product-item" onClick={() => handleProductClick(product.productId)}>
                    <img src={product.imageUrls} alt={product.productName} className="product-image" />
                    <CartItem productName={product.name} price={product.price} />
                </div>
            ))}
        </div>
    );
};

export default ProductList;
