import axios from 'axios';

const baseLink = 'http://localhost:5163';

const songApi = axios.create({
    baseURL: `${baseLink}/api/Song`, // Specify the base URL for SongController API
    headers: {
        'Content-Type': 'multipart/form-data' // Use multipart/form-data for file uploads
    }
});
songApi.interceptors.request.use((config) => {
    console.log("Interceptor hit for URL:", config.url); // Debugging
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
}, (error) => {
    return Promise.reject(error);
});
const getSongById = async (id) => {
    try {
        const response = await songApi.get(`song/${id}`);
        console.log("get song by id ",response.data)
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};


const deleteSongById = async (id) => {
    try {
        const response = await songApi.delete(`${id}`);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const getAllSongsByUserId = async (userId) => {
    try {
        const response = await songApi.get(`user/${userId}`);
        const songs = response.data;

        const pageSize = 10; // Number of songs per page
        const paginatedSongs = [];
        for (let i = 0; i < songs.length; i += pageSize) {
            paginatedSongs.push(songs.slice(i, i + pageSize));
        }

        return paginatedSongs;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
}

const getAllSongs = async () => {
    try {
        const response = await songApi.get('');
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

// Function to search songs by title
const searchByTitle = async (title) => {
    try {
        const response = await songApi.get('search', {
            params: { title }
        });
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

// Function to add a new song
const addNewSong = async (songData) => {
    try {
        const response = await songApi.post('', songData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

// Function to update a song by ID
const updateSongById = async (id, updatedSongData) => {
    try {
        const response = await songApi.put(`${id}`, updatedSongData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

// Function to delete a song by ID


