import React, { useState, useEffect, useRef } from "react";
import AudioControls from "./AudioControls";
import moreOption from '../Icon/icons8-more-50.png'
import mute from '../Icon/icons8-mute-50.png';
import lowVolume from '../Icon/icons8-low-volume-50.png';
import middleVolume from '../Icon/icons8-volume-50.png';
import volumeUp from '../Icon/icons8-volume-up-50.png';


const AudioPlayer = ({ tracks }) => {
    // State and ref setup
    const [trackIndex, setTrackIndex] = useState(0);
    const [trackProgress, setTrackProgress] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [volume, setVolume] = useState(1.0);
    const [muted, setMuted] = useState(false);
    const audioRef = useRef(new Audio(tracks[trackIndex].audioSrc));
    const intervalRef = useRef();
    const { title, artist, image } = tracks[trackIndex];

    // Utility functions
    const startTimer = () => {
        // Clear any timers already running
        clearInterval(intervalRef.current);

        intervalRef.current = setInterval(() => {
            if (audioRef.current.ended) {
                toNextTrack();
            } else {
                setTrackProgress(audioRef.current.currentTime);
            }
        }, [1000]);
    };

    const onScrub = (value) => {
        // Clear any timers already running
        clearInterval(intervalRef.current);
        audioRef.current.currentTime = value;
        setTrackProgress(audioRef.current.currentTime);
    };

    const onScrubEnd = () => {
        // If not already playing, start
        if (!isPlaying) {
            setIsPlaying(true);
        }
        startTimer();
    };

    // Navigation functions for the player
    const toPrevTrack = () => {
        if (trackIndex - 1 < 0) {
            setTrackIndex(tracks.length - 1);
        } else {
            setTrackIndex(trackIndex - 1);
        }
    };

    const toNextTrack = () => {
        if (trackIndex < tracks.length - 1) {
            setTrackIndex(trackIndex + 1);
        } else {
            setTrackIndex(0);
        }
    };

    // Volume control functions
    const changeVolume = (newVolume) => {
        audioRef.current.volume = newVolume;
        setVolume(newVolume);
        setMuted(newVolume === 0);
    };

    const toggleMute = () => {
        const newMutedState = !muted;
        setMuted(newMutedState);
        audioRef.current.muted = newMutedState;
        if (!newMutedState) {
            audioRef.current.volume = volume;
        }
    };

    // Effect hooks
    useEffect(() => {
        // Update the audio element when track changes
        audioRef.current.pause();
        audioRef.current = new Audio(tracks[trackIndex].audioSrc);
        setTrackProgress(0);

        if (isPlaying) {
            audioRef.current.play().then(() => startTimer()).catch((error) => console.error('Error attempting to play', error));
        }

        return () => {
            audioRef.current.pause();
            clearInterval(intervalRef.current);
        };
    }, [trackIndex]);

    useEffect(() => {
        if (isPlaying) {
            audioRef.current.play().catch((error) => console.error('Error attempting to play', error));
        } else {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    useEffect(() => {
        audioRef.current.volume = volume;
        audioRef.current.muted = muted;
    }, [volume, muted]);

    // Function to update volume icon based on current volume
    const getVolumeIcon = () => {
        if (muted || volume === 0) return muteIcon;
        if (volume < 0.5) return lowVolumeIcon;
        if (volume < 1) return middleVolumeIcon;
        return volumeUpIcon;
    };

    // Inline styles for player sections
    const playerContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#000000',
        color: '#ffffff',
        position: 'fixed',
        bottom: '0',
        left: '0',
        right: '0',
        width: '100%',
        maxWidth: '1920px',
        margin: '0 auto',
    };

    const progressBarStyle = {
        width: '100%',
    };

    const volumeIcon = getVolumeIcon();


    return (
        <div className="audio-player" style={playerContainerStyle}>
            <div style={{ display: 'flex', alignItems: 'center', marginLeft: '20px' }}>
                <img
                    src={image}
                    alt={`Track artwork for ${title} by ${artist}`}
                    style={{ width: '80px', height: '80px', borderRadius: '80px' }}
                />
                <div style={{ marginLeft: '10px' }}>
                    <h2 style={{ margin: '0' }}>{title}</h2>
                    <p style={{ margin: '0' }}>{artist}</p>
                </div>
            </div>

            <div style={centerSectionStyle}>
                <AudioControls
                    isPlaying={isPlaying}
                    onPrevClick={toPrevTrack}
                    onNextClick={toNextTrack}
                    onPlayPauseClick={handlePlayClick}
                />
                <input
                    type="range"
                    value={trackProgress}
                    step="1"
                    min="0"
                    max={duration || 0}
                    className="progress"
                    onChange={(e) => onScrub(e.target.value)}
                    onMouseUp={onScrubEnd}
                    onKeyUp={onScrubEnd}
                    style={progressBarStyle}
                />
            </div>

            <div style={rightSectionStyle}>
                <img
                    src={muted ? mute : volumeIconPath}
                    alt="Volume control"
                    onClick={toggleMute}
                    style={{ width: '30px', height: '30px', cursor: 'pointer' }}
                />
                <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.01"
                    value={muted ? 0 : volume}
                    onChange={(e) => changeVolume(parseFloat(e.target.value))}
                    style={{ width: '70px' }}
                />
                <img
                    src={moreOption}
                    alt="More options"
                    style={{ width: '30px', height: '30px' }}
                />
            </div>
        </div>
    );
};

export default AudioPlayer;
