import React, { useState, useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, getSongById } from '../../Api/SongApi';

const EditSong = () => {
    const { songId } = useParams();
    const navigate = useNavigate();
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const [song, setSong] = useState({
        title: '',
        imageFile: null,
        soundtrackFile: null,
        genre: '',
        privacy: 'public',
        imageFilePreview: '',
        soundtrackFilePreview: '',
    });

    // Flags to track if the user wants to remove the existing files
    const [removeImageFile, setRemoveImageFile] = useState(false);
    const [removeSoundtrackFile, setRemoveSoundtrackFile] = useState(false);

    // Map genre names to integers and vice versa
    const genreToIntMap = useMemo(() => ({
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    }), []);

    const intToGenreMap = useMemo(() =>
            Object.keys(genreToIntMap).reduce((obj, key) => {
                obj[genreToIntMap[key]] = key;
                return obj;
            }, {}),
        [genreToIntMap]);

    useEffect(() => {
        const fetchSong = async () => {
            try {
                const songData = await getSongById(songId);
                setSong(prevState => ({
                    ...prevState,
                    title: songData.title,
                    genre: intToGenreMap[songData.genreId],
                    privacy: songData.privacy ? 'public' : 'private',
                    imageFilePreview: `data:image/png;base64,${songData.imageData}`,
                    soundtrackFilePreview: `data:audio/mpeg;base64,${songData.soundTrackData}`,
                }));
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId, intToGenreMap]);

    const handleFileChange = (event) => {
        const { name, files } = event.target;
        if (files.length > 0) {
            const fileUrl = URL.createObjectURL(files[0]);
            setSong({ ...song, [name]: files[0], [`${name}Preview`]: fileUrl });
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setSong({ ...song, [name]: value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('title', song.title);
        formData.append('genreId', genreToIntMap[song.genre]);
        formData.append('privacy', song.privacy === 'public');
        formData.append('userId', user.Id);

        if (!removeImageFile && song.imageFile) {
            formData.append('imageFile', song.imageFile);
        } else if (removeImageFile) {
            formData.append('removeImageFile', true);
        }

        if (!removeSoundtrackFile && song.soundtrackFile) {
            formData.append('soundtrackFile', song.soundtrackFile);
        } else if (removeSoundtrackFile) {
            formData.append('removeSoundtrackFile', true);
        }

        for (let [key, value] of formData.entries()) {
            console.log(`${key}:`, value);
        }

        try {
            await updateSongById(songId, formData);
            alert('Song updated successfully');
            navigate('/ListSong');
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    return (
        <div style={{ maxWidth: '600px', margin: 'auto', padding: '20px', background: '#f9f9f9', borderRadius: '10px', boxShadow: '0 0 10px rgba(0,0,0,0.1)' }}>
            <h2 style={{ textAlign: 'center' }}>Edit Song</h2>
            <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="title">Title:</label>
                    <input
                        id="title"
                        type="text"
                        name="title"
                        value={song.title}
                        onChange={handleChange}
                        placeholder="Enter song title"
                        style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                    />
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="genre">Genre:</label>
                    <select
                        id="genre"
                        name="genre"
                        value={song.genre}
                        onChange={handleChange}
                        style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                    >
                        {Object.entries(intToGenreMap).map(([key, value]) => (
                            <option key={key} value={value}>{value}</option>
                        ))}
                    </select>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="privacy">Privacy:</label>
                    <select
                        id="privacy"
                        name="privacy"
                        value={song.privacy}
                        onChange={handleChange}
                        style={{ width: '100%', padding: '10px', border: '1px solid #ccc', borderRadius: '5px' }}
                    >
                        <option value="public">Public</option>
                        <option value="private">Private</option>
                    </select>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="imageFile">Image:</label>
                    <input
                        id="imageFile"
                        type="file"
                        name="imageFile"
                        onChange={handleFileChange}
                        style={{ display: 'block', marginBottom: '10px' }}
                    />
                    {song.imageFilePreview && !removeImageFile && (
                        <img src={song.imageFilePreview} alt="Song" style={{ width: '150px', height: '150px', borderRadius: '4px' }} />
                    )}
                    <label>
                        <input
                            type="checkbox"
                            checked={removeImageFile}
                            onChange={() => setRemoveImageFile(!removeImageFile)}
                        /> Remove existing image
                    </label>
                </div>
                <div style={{ marginBottom: '15px' }}>
                    <label htmlFor="soundtrackFile">Soundtrack:</label>
                    <input
                        id="soundtrackFile"
                        type="file"
                        name="soundtrackFile"
                        onChange={handleFileChange}
                        style={{ display: 'block', marginBottom: '10px' }}
                    />
                    {song.soundtrackFilePreview && !removeSoundtrackFile && (
                        <div style={{ width: '100%', marginTop: '10px' }}>
                            <audio controls>
                                <source src={song.soundtrackFilePreview} type="audio/mpeg" />
                            </audio>
                        </div>
                    )}
                    <label>
                        <input
                            type="checkbox"
                            checked={removeSoundtrackFile}
                            onChange={() => setRemoveSoundtrackFile(!removeSoundtrackFile)}
                        /> Remove existing soundtrack
                    </label>
                </div>
                <div>
                    <button type="submit" style={{ marginRight: '10px', padding: '10px 20px', border: 'none', background: '#007bff', color: '#fff', borderRadius: '5px', cursor: 'pointer' }}>Save Changes</button>
                    <button type="button" onClick={() => navigate('/ListSong')} style={{ padding: '10px 20px', border: 'none', background: '#dc3545', color: '#fff', borderRadius: '5px', cursor: 'pointer' }}>Cancel</button>
                </div>
            </form>
        </div>
    );
};

export default EditSong;
