import axios from 'axios';

const baseLink = 'http://localhost:5163';

const authApi = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'application/json'
    }
});

authApi.interceptors.request.use((config) => {
    console.log("Interceptor hit for URL:", config.url); // Debugging
    const token = localStorage.getItem('accessToken');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
}, (error) => {
    return Promise.reject(error);
});

async function checkAndRefreshToken(apiResponse) {
    if (apiResponse.status === 401 && apiResponse.headers.get('Token-Expired')) {
        const refreshToken = JSON.parse(localStorage.getItem('refreshToken') || '{}');
        const refreshResponse = await fetch('/Account/refresh-token', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ Token: refreshToken })
        });

        if (refreshResponse.ok) {
            const data = await refreshResponse.json();
            localStorage.setItem('accessToken', data.data);
        } else {
        }
    }
}

const login = async (credentials) => {
    try {
        const response = await axios.post('http://localhost:5163/Account/login', credentials);
        localStorage.setItem('accessToken', response.data.accessToken);
        localStorage.setItem('refreshToken', response.data.refreshToken);
        localStorage.setItem('user',response.data.user)
        localStorage.setItem('role',response.data.role)
        console.log('User logged in successfully.');
        return response.data; // Return the response data
    } catch (error) {
        console.error('Error:', error);
        throw error; // Throwing the error for further handling if necessary
    }
};

const register = async (userData) => {
    try {
        console.log("Register data", userData);
        const response = await authApi.post('/Account/register', userData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const getAuthorizedMessage = async () => {
    try {
        const response = await authApi.get('/User/authorized');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const getUserOnlyMessage = async () => {
    try {
        const response = await authApi.get('/User/user-only');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
export { login, register,getAuthorizedMessage,getUserOnlyMessage };
