import React, { useState } from 'react';
import { login } from "../Api/api";
import { Navigate } from "react-router-dom";

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [rememberMe, setRememberMe] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [loggedIn, setLoggedIn] = useState(false);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const credentials = {
            email,
            password,
            rememberMe
        };

        const data = await login(credentials);
        if (data && !data.error) { // Assuming the presence of `data.error` indicates an error response
            console.log('User logged in successfully.');
            setLoggedIn(true);
        } else if (data && data.error) {
            setErrorMessage(data.error);
        }
    };

    if (loggedIn) {
        return <Navigate to="/" />;
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ margin: '20px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', maxWidth: '400px' }}>
                <h2>Login</h2>
                {errorMessage && <div style={{ color: 'red', marginBottom: '10px' }}>{errorMessage}</div>}
                <form onSubmit={handleSubmit}>
                    <div style={{ marginBottom: '10px' }}>
                        <label>Email:</label>
                        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                        <label>Password:</label>
                        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                    </div>
                    <div style={{ marginBottom: '10px' }}>
                        <label>
                            <input type="checkbox" checked={rememberMe} onChange={(e) => setRememberMe(e.target.checked)} />
                            Remember me
                        </label>
                    </div>
                    <button type="submit" style={{ padding: '5px 10px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '3px', cursor: 'pointer' }}>Login</button>
                </form>
            </div>
        </div>
    );
};

export default Login;
