import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [user, setUser] = useState(null);
    const [showDropdown, setShowDropdown] = useState(false);
    const [clickedOutside, setClickedOutside] = useState(false); // State to track if clicked outside

    const onLogout = () => {
        localStorage.removeItem('user');
        localStorage.removeItem('token');
        setIsAuthenticated(false);
        setUser(null);
        window.location.href = '/'; // Navigate to home page
    };

    useEffect(() => {
        const user = localStorage.getItem('user');
        if (user) {
            setIsAuthenticated(true);
            setUser(JSON.parse(user));
        }
    }, []);

    const toggleDropdown = () => {
        setShowDropdown(!showDropdown);
    };

    // Close dropdown if clicked outside
    useEffect(() => {
        const closeDropdown = (e) => {
            const dropdown = document.querySelector('.dropdown-content');
            const username = document.querySelector('.username');
            if (!dropdown.contains(e.target) && e.target !== username) {
                setShowDropdown(false);
                setClickedOutside(true); // Set state to true when clicked outside
            } else {
                setClickedOutside(false); // Reset state to false if clicked inside
            }
        };

        document.body.addEventListener('click', closeDropdown);
        return () => document.body.removeEventListener('click', closeDropdown);
    }, []);

    return (
        <nav>
            <div className="navbar-container">
                <div className="navbar-brand">
                    <Link to="/">WebName</Link>
                </div>
                <ul className="navbar-menu">
                    {!isAuthenticated && (
                        <>
                            <li>
                                <Link to="/register">Register</Link>
                            </li>
                            <li>
                                <Link to="/login">Login</Link>
                            </li>
                        </>
                    )}
                    {isAuthenticated && user && (
                        <li className="dropdown">
                            <span className="username" onClick={toggleDropdown}>{user.UserName}</span>
                            {showDropdown && (
                                <div className="dropdown-content">
                                    <Link to="/account">Account</Link>
                                    <Link to="/settings">Settings</Link>
                                    <button onClick={onLogout}>Logout</button>
                                </div>
                            )}
                        </li>
                    )}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
