import React, { useState, useEffect } from 'react';
import { getOrderDetail } from '../../utils/Api/OrderApi';
import { useParams } from 'react-router-dom';
import {getProductById} from "../../utils/Api/ProductApi";

const OrderDetailPage = () => {
    const { orderId } = useParams(); // Extract orderId from the URL
    const [orderDetail, setOrderDetail] = useState(null);

    useEffect(() => {
        if (orderId) {
            fetchOrderDetail(orderId);
        }
    }, [orderId]);

    const fetchOrderDetail = async (orderId) => {
        try {
            const orderDetailData = await getOrderDetail(orderId);
            setOrderDetail(orderDetailData);

            // Fetch product details for each order item
            const productDetailPromises = orderDetailData.orderItems.map(item =>
                getProductById(item.productId)
            );
            const productDetailsData = await Promise.all(productDetailPromises);
            setProductDetails(productDetailsData.reduce((acc, product, index) => {
                acc[orderDetailData.orderItems[index].productId] = product;
                return acc;
            }, {}));

        } catch (error) {
            console.error('Error fetching order detail:', error);
        }
    };

    if (!orderDetail) {
        return <p>Loading...</p>;
    }

    return (
        <div className="order-detail-container">
            <h1>Order Detail</h1>
            <p><strong>Email:</strong> {orderDetail.email}</p>
            <p><strong>Total Price:</strong> ${orderDetail.totalPrice.toFixed(2)}</p>
            <p><strong>Date:</strong> {new Date(orderDetail.dateTime).toLocaleString()}</p>
            <p><strong>Phone Number:</strong> {orderDetail.phoneNumber}</p>
            <p><strong>Status:</strong> {orderDetail.status}</p>

            <h2>Order Items</h2>
            {orderDetail.orderItems.map((item, index) => (
                <div key={index} className="order-item">
                    <p><strong>Product ID:</strong> {item.productId}</p>
                    <p><strong>Size:</strong> {item.size}</p>
                    <p><strong>Quantity:</strong> {item.quantity}</p>
                    <p><strong>Total Price:</strong> ${item.totalPrice.toFixed(2)}</p>
                </div>
            ))}
        </div>
    );
};

export default OrderDetailPage;
