import React, { useEffect, useRef, useState } from 'react';
import AudioControls from './AudioControls';
import muteIcon from '../Icon/icons8-mute-50.png';
import lowVolumeIcon from '../Icon/icons8-low-volume-50.png';
import middleVolumeIcon from '../Icon/icons8-volume-50.png';
import volumeUpIcon from '../Icon/icons8-volume-up-50.png';

const AudioPlayer = ({ track, onPlayPauseClick }) => {
    // Ref for the audio component
    const audioRef = useRef(new Audio(track.audioSrc));
    const [isReady, setIsReady] = useState(false);
    const [volume, setVolume] = useState(1); // Volume state
    const [isMuted, setIsMuted] = useState(false); // Mute state

    // Update the audio source when the track changes
    useEffect(() => {
        if (track && track.audioSrc) {
            audioRef.current.src = track.audioSrc;
            audioRef.current.load();
            setIsReady(true);
        }
    }, [track]);

    // Handle play/pause click
    useEffect(() => {
        if (isReady) {
            onPlayPauseClick ? audioRef.current.play() : audioRef.current.pause();
        }
    }, [onPlayPauseClick, isReady]);

    // Handle volume change
    useEffect(() => {
        audioRef.current.volume = isMuted ? 0 : volume;
    }, [volume, isMuted]);

    // Clean up
    useEffect(() => {
        return () => {
            audioRef.current.pause();
            setIsReady(false);
        };
    }, []);

    if (!track) return null; // Don't display the player if there's no track

    // Function to toggle mute
    const toggleMute = () => {
        setIsMuted(!isMuted);
    };

    // Get volume icon based on volume level
    const getVolumeIcon = () => {
        if (isMuted || volume === 0) return muteIcon;
        if (volume < 0.5) return lowVolumeIcon;
        if (volume < 1) return middleVolumeIcon;
        return volumeUpIcon;
    };

    // Render the audio player
    return (
        <footer style={{ width: '100%', position: 'fixed', bottom: 0, backgroundColor: '#fff', boxShadow: '0 -2px 5px rgba(0, 0, 0, 0.2)' }}>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '1rem' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img src={track.image} alt={track.title} style={{ width: '50px', height: '50px', marginRight: '1rem', borderRadius: '50%' }} />
                    <div>
                        <h3 style={{ marginBottom: '0.5rem' }}>{track.title}</h3>
                        <p style={{ margin: 0 }}>{track.artist}</p>
                    </div>
                </div>
                <AudioControls
                    isPlaying={onPlayPauseClick}
                    onPlayPauseClick={() => onPlayPauseClick(!onPlayPauseClick)}
                />
                <div>
                    {/* Volume control */}
                    <img
                        src={getVolumeIcon()}
                        alt="Volume"
                        onClick={toggleMute}
                        style={{ width: '30px', height: '30px', cursor: 'pointer', marginRight: '0.5rem' }}
                    />
                    <input
                        type="range"
                        value={isMuted ? 0 : volume}
                        min="0"
                        max="1"
                        step="0.01"
                        onChange={(e) => setVolume(parseFloat(e.target.value))}
                        style={{ margin: '0 15px', width: '100px' }}
                    />
                    <button onClick={toggleMute}>{isMuted ? 'Unmute' : 'Mute'}</button>
                </div>
            </div>
            <audio ref={audioRef} onEnded={() => onPlayPauseClick(false)} />
        </footer>
    );
};

export default AudioPlayer;
