import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getAllAlbumByUserId } from '../../Api/ArtistApi';

const ListAlbum = () => {
    const [albums, setAlbums] = useState([]);
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    const [showDropdown, setShowDropdown] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (!user || !user.Id) {
            console.error('User ID is missing');
            return;
        }
        getAllAlbumByUserId(user.Id)
            .then(fetchedAlbums => {
                setAlbums(fetchedAlbums.flat());
            })
            .catch(error => console.error('Error fetching albums:', error));
    }, [user.Id]);

    // const handleDelete = async (albumId) => {
    //     try {
    //         await deleteAlbumById(albumId);
    //         setAlbums(albums.filter(album => album.albumId !== albumId));
    //     } catch (error) {
    //         console.error('Error deleting the album:', error);
    //     }
    // };

    const handleEdit = (albumId) => {
        console.log(`Editing album with ID: ${albumId}`);
        navigate(`/EditAlbum/${albumId}`);
    };

    return (
        <div>
            <h2>Albums by User</h2>
            <ul>
                {albums.map((album) => (
                    <li key={album.albumId} className="album-item">
                        <div className="album-info">
                            <div className="album-details">
                                <strong>Title:</strong> {album.nameOfAlbum}
                            </div>
                            <button onClick={() => handleEdit(album.albumId)} className="edit-button">
                                Edit
                            </button>
                            {/*<button onClick={() => handleDelete(album.albumId)} className="delete-button">*/}
                            {/*    Delete*/}
                            {/*</button>*/}
                        </div>
                    </li>
                ))}
            </ul>
            <style>
                {`
                .navbar-container {
                  display: flex;
                  justify-content: space-between;
                  align-items: center;
                  padding: 1rem;
                  background-color: #333;
                  color: #fff;
                }
                .navbar-menu {
                  list-style: none;
                  display: flex;
                  align-items: center;
                }
                .navbar-menu li {
                  margin-left: 1rem;
                }
                .navbar-menu li:first-child {
                  margin-left: 0;
                }
                .navbar-menu a {
                  color: #fff;
                  text-decoration: none;
                }
                button {
                  padding: 10px 20px;
                  margin: 5px;
                  background-color: #007bff;
                  color: #fff;
                  border: none;
                  border-radius: 5px;
                  cursor: pointer;
                }
                button:hover {
                  background-color: #0056b3;
                }
                `}
            </style>
        </div>
    );
};

export default ListAlbum;
