import React, { useState, useEffect } from 'react';
import './AttributeLayout.css';
import { filterProductApi } from '../../utils/Api/SearchProductApi'; // Import the filterProductApi function
import ProductList from '../Product/ProductList';

const AttributeLayout = () => {
    const [selectedOptions, setSelectedOptions] = useState({
        design: null,
        product: null,
        material: null,
        color: null,
        price: null
    });
    const [filteredProducts, setFilteredProducts] = useState([]);

    useEffect(() => {
        const fetchFilteredProducts = async () => {
            try {
                console.log('Selected options sent to API: ', selectedOptions);
                const filteredData = await filterProductApi(selectedOptions);
                setFilteredProducts(filteredData || []);
            } catch (error) {
                console.error('Error filtering products:', error);
            }
        };

        fetchFilteredProducts();
    }, [selectedOptions]);

    const sections = [
        {
            title: 'Design',
            options: ['Low Top', 'High Top', 'Mule']
        }, {
            title: 'Product',
            options: ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces']
        }, {
            title: 'Material',
            options: ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy']
        }, {
            title: 'Color',
            options: [
                { color: '#808080', name: 'Gray' },
                { color: '#ffffff', name: 'White' },
                { color: '#ffffcc', name: 'Cream' },
                { color: '#36454f', name: 'Charcoal' },
                { color: '#008080', name: 'Teal' },
                { color: '#f5f5dc', name: 'Beige' },
                { color: '#c0c0c0', name: 'Silver' },
                { color: '#000080', name: 'NavyBlue' },
                { color: '#808000', name: 'OliveGreen' },
                { color: '#e6e6fa', name: 'Lavender' },
                { color: '#ff7f50', name: 'Coral' },
                { color: '#ed9121', name: 'CarrotOrange' },
                { color: '#dc143c', name: 'CrimsonRed' }
            ]
        }, {
            title: 'Price',
            options: ['Under 200', '200 - 299', '300 - 399', '400 - 499', '500 - 599', '> 600']
        }
    ];

    const mapSelectedOptionsToIndexes = (selectedOptions) => {
        const indexes = {};
        Object.keys(selectedOptions).forEach((key) => {
            const optionIndex = sections.find(section => section.title.toLowerCase() === key).options.indexOf(selectedOptions[key]);
            indexes[key] = optionIndex;
        });
        return indexes;
    };

    const handleOptionSelection = (section, option, index) => {
        const selectedValue = option.trim() === '' ? null : index;
        setSelectedOptions(prevOptions => ({
            ...prevOptions,
            [section.toLowerCase()]: selectedValue
        }));
    };


    return (
        <div className="attribute-layout">
            {/* Sections rendering */}
            {sections.map((section, index) => (
                <div className="section" key={index}>
                    <h3>{section.title}</h3>
                    <ul>
                        {/* Options rendering */}
                        {section.options.map((option, optionIndex) => {
                            const isColorSection = section.title === 'Color';
                            const isSelected = selectedOptions[section.title.toLowerCase()] === optionIndex;
                            return (
                                <li
                                    key={optionIndex}
                                    onClick={() => handleOptionSelection(section.title.toLowerCase(), option, optionIndex)}
                                    className={isSelected ? 'selected' : ''}
                                >
                                    {/* Rendering color options */}
                                    {isColorSection && (
                                        <>
                                            <span className="color-swatch" style={{ backgroundColor: option.color }}></span>
                                            <span className="color-name">{option.name}</span>
                                        </>
                                    )}
                                    {/* Rendering non-color options */}
                                    {!isColorSection && option}
                                </li>
                            );
                        })}
                    </ul>
                </div>
            ))}
            {filteredProducts.length > 0 ? (
                <ProductList products={filteredProducts} />
            ) : (
                <div>No products found</div>
            )}
        </div>
    );
};

export default AttributeLayout;
