import { useParams } from 'react-router-dom';
import {useEffect, useState} from "react";
import {getProductById} from "../../utils/Api/ProductApi";

const ProductDetail = () => {
    const { productId } = useParams();
    const [product, setProduct] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const productData = await getProductById(productId);
                console.log('product detail: ',productData)
                setProduct(productData);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };
        if (productId) {
            fetchProduct();
        }
    }, [productId]);

    if (!product) {
        return <div>Loading...</div>;
    }

    return (
        <div style={{ display: 'flex', justifyContent: 'center', backgroundColor: '#f5f5f5', padding: '20px' }}>

            <div style={{ flex: 1 }}>
                <img src={product.imageUrls} alt={product.name} style={{ maxWidth: '100%' }} />
            </div>
            <div style={{ flex: 1, marginLeft: '20px' }}>
                <h2>{product.name}</h2>
                <p>Description: {product.description}</p>
                <p>Price: {product.price}</p>
            </div>
        </div>
    );
};

export default ProductDetail;
