import React from 'react';
import defaultImage from '../../Img/DefaultAvatar.png';

const ProductPage = () => {
    const productList = [
        { id: 1, name: 'Product 1', inStock: true },
        { id: 2, name: 'Product 2', inStock: false },
        { id: 3, name: 'Product 3', inStock: true },
    ];

    const selectedProduct = null; // No data selected in this static view

    return (
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            {/* Product List on the Left */}
            <div style={{ width: '50%' }}>
                <h1>Products</h1>
                <ul style={{ listStyle: 'none', padding: 0 }}>
                    {productList.map((product) => (
                        <li key={product.id} style={{ cursor: 'pointer', marginBottom: '10px' }}>
                            {product.name} - {product.inStock ? 'In Stock' : 'Out of Stock'}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Product Details on the Right */}
            <div style={{ width: '50%' }}>
                {selectedProduct ? (
                    <div>
                        <h2>{selectedProduct.name}</h2>
                        <img src={defaultImage} alt="Product" />
                        {/* Display other details of selectedProduct here */}
                    </div>
                ) : (
                    <p>No product selected</p>
                )}
            </div>
        </div>
    );
};

export default ProductPage;
