import React, { useState, useEffect, useRef } from "react";
import AudioControls from "./AudioControls";
import moreOption from '../Icon/icons8-more-50.png'
import muteIcon from '../Icon/icons8-mute-50.png';
import lowVolumeIcon from '../Icon/icons8-low-volume-50.png';
import middleVolumeIcon from '../Icon/icons8-volume-50.png';
import volumeUpIcon from '../Icon/icons8-volume-up-50.png';


const AudioPlayer = ({ tracks }) => {
    const [trackIndex, setTrackIndex] = useState(tracks && tracks.length > 0 ? 0 : null);
    const [trackProgress, setTrackProgress] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const [volume, setVolume] = useState(1.0);
    const [muted, setMuted] = useState(false);
    const audioRef = useRef(null);
    const intervalRef = useRef();
    const isReady = useRef(false);

    // Destructure for easier access
    const track = trackIndex !== null ? tracks[trackIndex] : undefined;
    const { title, artist, image, audioSrc } = track || {};

    useEffect(() => {
        if (trackIndex !== null) {
            audioRef.current = new Audio(audioSrc);
            setTrackProgress(audioRef.current.currentTime);

            if (isPlaying) {
                audioRef.current.play().catch((error) => {
                    console.error('Error attempting to play', error);
                });
            }
        }
    }, [trackIndex, tracks]);

    useEffect(() => {
        if (isPlaying && audioRef.current) {
            audioRef.current.play().catch((error) => {
                console.error('Error attempting to play', error);
            });
        } else if (audioRef.current) {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    useEffect(() => {
        if (audioRef.current) {
            audioRef.current.volume = volume;
            audioRef.current.muted = muted;
        }
    }, [volume, muted]);

    useEffect(() => {
        if (audioRef.current) {
            const handleEnded = () => {
                if (tracks.length && trackIndex !== null) {
                    toNextTrack();
                } else {
                    setIsPlaying(false);
                }
            };

            audioRef.current.addEventListener('ended', handleEnded);

            return () => {
                audioRef.current.removeEventListener('ended', handleEnded);
            };
        }
    }, [tracks, trackIndex]);

    const toPrevTrack = () => {
        if (trackIndex === 0) {
            setTrackIndex(tracks.length - 1);
        } else if (trackIndex) {
            setTrackIndex(trackIndex - 1);
        }
    };

    const toNextTrack = () => {
        if (trackIndex !== null && trackIndex < tracks.length - 1) {
            setTrackIndex(trackIndex + 1);
        } else {
            setTrackIndex(0);
        }
    };

    const getVolumeIcon = () => {
        if (muted || volume === 0) return muteIcon;
        if (volume < 0.5) return lowVolumeIcon;
        if (volume < 1) return middleVolumeIcon;
        return volumeUpIcon;
    };

    // Render nothing if no track is selected
    if (trackIndex === null) {
        return <div>No track selected</div>;
    }
    const onScrub = (value) => {
        // Clear any timers already running
        clearInterval(intervalRef.current);
        audioRef.current.currentTime = value;
        setTrackProgress(audioRef.current.currentTime);
    };

    const onScrubEnd = () => {
        // If not already playing, start
        if (!isPlaying) {
            setIsPlaying(true);
        }
        startTimer();
    };
    const toggleMute = () => {
        const newMutedState = !muted;
        setMuted(newMutedState);
        if (audioRef.current) {
            audioRef.current.muted = newMutedState;
        }
    };



    const playerContainerStyle = {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#000000',
        color: '#ffffff',
        position: 'fixed',
        bottom: '0',
        left: '0',
        right: '0',
        width: '100%',
        maxWidth: '1920px',
        margin: '0 auto',
    };

    const progressBarStyle = {
        width: '100%',
    };

    const volumeIcon = getVolumeIcon();


    return (
        <div className="audio-player" style={playerContainerStyle}>
            <div style={{ display: 'flex', alignItems: 'center', marginLeft: '20px' }}>
                <img
                    src={image}
                    alt={`Track artwork for ${title} by ${artist}`}
                    style={{ width: '80px', height: '80px', borderRadius: '80px' }}
                />
                <div style={{ marginLeft: '10px' }}>
                    <h2 style={{ margin: '0' }}>{title}</h2>
                    <p style={{ margin: '0' }}>{artist}</p>
                </div>
            </div>

            <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <AudioControls
                    isPlaying={isPlaying}
                    onPlayPauseClick={setIsPlaying}
                    onPrevClick={toPrevTrack}
                    onNextClick={toNextTrack}
                />
                <input
                    type="range"
                    value={trackProgress}
                    step="1"
                    min="0"
                    max={audioRef.current.duration || 0}
                    className="progress"
                    onChange={(e) => onScrub(e.target.value)}
                    onMouseUp={onScrubEnd}
                    onKeyUp={onScrubEnd}
                    style={progressBarStyle}
                />
            </div>

            <div style={{ display: 'flex', flexDirection: 'row', alignItems: 'center', marginRight: '20px' }}>
                <img
                    src={volumeIcon}
                    alt="Volume"
                    onClick={toggleMute}
                    style={{ width: '30px', height: '30px', cursor: 'pointer' }}
                />
                <input
                    type="range"
                    min="0"
                    max="1"
                    step="0.01"
                    value={muted ? 0 : volume}
                    onChange={(e) => changeVolume(parseFloat(e.target.value))}
                    style={{ margin: '0 15px', width: '100px' }}
                />
                <img
                    src={moreOption}
                    alt="More options"
                    style={{ width: '30px', height: '30px' }}
                />
            </div>
        </div>
    );
};

export default AudioPlayer;
