import React, { useState } from 'react';
import './AttributeLayout.css';

const AttributeLayout = () => {
    const [selectedOptions, setSelectedOptions] = useState({
        designs: '',
        products: '',
        materials: '',
        colors: '',
        prices: ''
    });

    const sections = [
        {
            title: 'Designs',
            options: ['Low Top', 'High Top', 'Slip-on', 'Mid Top', 'Mule']
        }, {
            title: 'Products',
            options: ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces']
        }, {
            title: 'Material',
            options: ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy']
        }, {
            title: 'Color',
            options: [
                { name: 'Gray', color: '#808080' },
                { name: 'White', color: '#ffffff' },
                { name: 'Cream', color: '#ffffcc' },
                { name: 'Charcoal', color: '#36454f' },
                { name: 'Teal', color: '#008080' },
                { name: 'Beige', color: '#f5f5dc' },
                { name: 'Silver', color: '#c0c0c0' },
                { name: 'NavyBlue', color: '#000080' },
                { name: 'OliveGreen', color: '#808000' },
                { name: 'Lavender', color: '#e6e6fa' },
                { name: 'Coral', color: '#ff7f50' },
                { name: 'CarrotOrange', color: '#ed9121' },
                { name: 'CrimsonRed', color: '#dc143c' }
            ]
        }, {
            title: 'Price',
            options: ['Under 200', '200 - 299', '300 - 399', '400 - 499', '500 - 599', '> 600']
        }
    ];

    const handleOptionSelection = (section, option) => {
        setSelectedOptions(prevOptions => {
            const newOptions = { ...prevOptions };
            if (newOptions[section] === option) {
                delete newOptions[section];
            } else {
                newOptions[section] = option;
            }
            return newOptions;
        });
    };

    return (
        <div className="attribute-layout">
            {sections.map((section, index) => (
                <div className="section" key={index}>
                    <h3>{section.title}</h3>
                    <ul>
                        {section.options.map((option, optionIndex) => (
                            <li
                                key={optionIndex}
                                onClick={() => handleOptionSelection(section.title.toLowerCase(), option.name || option)}
                                className={selectedOptions[section.title.toLowerCase()] === (option.name || option) ? 'selected' : ''}
                                style={section.title === 'Color' ? { backgroundColor: option.color } : {}}
                            >
                                {section.title === 'Color' ? <span className="color-swatch"></span> : null}
                            </li>
                        ))}
                    </ul>
                </div>
            ))}
        </div>
    );
};

export default AttributeLayout;
