import React, { useState } from 'react';
import './AttributeLayout.css';

const AttributeLayout = () => {
    const [selectedOptions, setSelectedOptions] = useState({
        designs: '',
        products: '',
        materials: '',
        colors: '',
        prices: ''
    });

    const sections = [
        {
            title: 'Designs',
            options: ['Low Top', 'High Top', 'Slip-on', 'Mid Top', 'Mule']
        }, {
            title: 'Products',
            options: ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces']
        }, {
            title: 'Material',
            options: ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy']
        }, {
            title: 'Color',
            options: [
                { color: '#808080' },
                { color: '#ffffff' },
                { color: '#ffffcc' },
                { color: '#36454f' },
                { color: '#008080' },
                { color: '#f5f5dc' },
                { color: '#c0c0c0' },
                { color: '#000080' },
                { color: '#808000' },
                { color: '#e6e6fa' },
                { color: '#ff7f50' },
                { color: '#ed9121' },
                { color: '#dc143c' }
            ]
        }, {
            title: 'Price',
            options: ['Under 200', '200 - 299', '300 - 399', '400 - 499', '500 - 599', '> 600']
        }
    ];

    const handleOptionSelection = (section, option) => {
        setSelectedOptions(prevOptions => {
            const newOptions = { ...prevOptions };
            if (newOptions[section] === option) {
                delete newOptions[section];
            } else {
                newOptions[section] = option;
            }
            return newOptions;
        });
    };

    return (
        <div className="attribute-layout">
            <div className="left-sections">
                {sections.slice(0, 3).map((section, index) => (
                    <div className="section" key={index}>
                        <h3>{section.title}</h3>
                        <ul>
                            {section.options.map((option, optionIndex) => (
                                <li
                                    key={optionIndex}
                                    onClick={() => handleOptionSelection(section.title.toLowerCase(), option)}
                                    className={selectedOptions[section.title.toLowerCase()] === (option.color || option) ? 'selected' : ''}
                                    style={section.title === 'Color' ? { backgroundColor: option.color } : {}}
                                >
                                    {section.title === 'Color' && <span className="color-swatch"></span>}
                                    {section.title !== 'Color' && (option.color ? null : option)}
                                </li>
                            ))}
                        </ul>
                    </div>
                ))}
            </div>
            <div className="right-sections">
                {sections.slice(3).map((section, index) => (
                    <div className="section" key={index}>
                        <h3>{section.title}</h3>
                        <ul>
                            {section.options.map((option, optionIndex) => (
                                <li
                                    key={optionIndex}
                                    onClick={() => handleOptionSelection(section.title.toLowerCase(), option.color || option)}
                                    className={selectedOptions[section.title.toLowerCase()] === (option.color || option) ? 'selected' : ''}
                                    style={section.title === 'Color' ? { backgroundColor: option.color } : {}}
                                >
                                    {section.title === 'Color' && <span className="color-swatch"></span>}
                                    {section.title !== 'Color' && (option.color ? null : option)}
                                </li>
                            ))}
                        </ul>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AttributeLayout;
