import React, { useState } from 'react';
import defaultImage from '../../Img/DefaultAvatar.png';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase"; // Ensure you have this set up
import { addProduct } from "../../utils/Api/ProductApi";
import { Guid } from "guid-typescript";

const ProductPage = () => {

    const [selectedColor, setSelectedColor] = useState(null);
    const [productList, setProductList] = useState([]); // Now product list is defined as an empty array
    const [productDetails, setProductDetails] = useState([]); // Define productDetails if needed
    const [image, setImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(null);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [design, setDesign] = useState(0); // Initialize with integer value
    const [productLine, setProductLine] = useState(0); // Initialize with integer value
    const [material, setMaterial] = useState(0); // Initialize with integer value
    const [color, setColor] = useState(0); // Initialize with integer value

    const Design = ['LowTop', 'HighTop', 'Mule'];
    const ProductLine = ['Basas', 'Vintas', 'Urbas', 'Pattas', 'Socks', 'Shoelaces'];
    const Material = ['Canvas', 'Suede', 'Leather', 'Flannel', 'Corduroy'];
    const Color = [
        'Gray', 'White', 'Cream', 'Charcoal', 'Teal', 'Beige', 'Silver', 'NavyBlue',
        'OliveGreen', 'Lavender', 'Coral', 'CarrotOrange', 'CrimsonRed'
    ];

    const designMap = { LowTop: 1, HighTop: 2, Mule: 3 };
    const productLineMap = { Basas: 1, Vintas: 2, Urbas: 3, Pattas: 4, Socks: 5, Shoelaces: 6 };
    const materialMap = { Canvas: 1, Suede: 2, Leather: 3, Flannel: 4, Corduroy: 5 };
    const colorMap = {
        Gray: 1, White: 2, Cream: 3, Charcoal: 4, Teal: 5, Beige: 6,
        Silver: 7, NavyBlue: 8, OliveGreen: 9, Lavender: 10, Coral: 11, CarrotOrange: 12, CrimsonRed: 13
    };

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            setImage(file);
            setImagePreview(URL.createObjectURL(file)); // Preview the image
        }
    };

    const handleSaveProduct = async () => {
        if (!image) {
            console.error('No image to upload');
            return;
        }

        const newProductId = Guid.create().toString(); // Generate a new Guid for the product
        const imageRef = ref(storage, `products/${newProductId}/${image.name}`);

        try {
            // Upload the image to Firebase
            const snapshot = await uploadBytes(imageRef, image);
            const imageUrl = await getDownloadURL(snapshot.ref);

            const productToSave = {
                name,
                description,
                design,
                productLine,
                material,
                color,
                ProductId: newProductId, // Use the Guid as the product ID
                ImageUrls: [imageUrl], // Save the image URL
            };

            const response = await addProduct(productToSave); // Save the product
            console.log('Product added successfully:', response);

        } catch (error) {
            console.error('Error saving product:', error);
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', maxWidth: '2000px', margin: '20px auto', gap: '20px', border: '1px solid #ccc' }}>
            {/* Product List */}
            <div style={{ width: '20%', background: 'white', padding: '20px', border: '1px solid #ccc' }}>
                <h1>Product List</h1>
                <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                    <thead>
                    <tr style={{ backgroundColor: '#f2f2f2' }}>
                        <th style={{ padding: '10px', textAlign: 'left' }}>ID</th>
                        <th style={{ padding: '5px', textAlign: 'left' }}>Name</th>
                        <th style={{ padding: '5px', textAlign: 'left' }}>Stock</th>
                    </tr>
                    </thead>
                    <tbody>
                    </tbody>
                </table>
            </div>

            {/* Product Details */}
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', width: '80%', background: 'white', padding: '10px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                {/* Left Section */}
                <div style={{ width: '80%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    {/* buttons */}
                    <button onClick={handleSaveProduct} style={{ backgroundColor: 'blue', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Add</button>
                    <button style={{ backgroundColor: 'red', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Delete</button>
                    <button onClick={handleSaveProduct} style={{ backgroundColor: 'green', color: 'white', padding: '15px', fontSize: '20px', fontWeight: 'bold' }}>Save</button>
                    {/* information */}
                    <div style={{ display: 'flex', marginBottom: '20px', border: '1px solid #ccc', alignItems: 'flex-start' }}>
                        <input type="file" accept="image/*" onChange={handleImageUpload} style={{ display: 'none' }} id="imageUpload" />
                        <label htmlFor="imageUpload" style={{ width: '30%', marginRight: '5px' }}>
                            {imagePreview && (
                                <div style={{ width: '300px', height: '300px', border: '1px solid #ccc' }}>
                                    <img src={imagePreview} alt="Preview" style={{ width: '300px', height: '300px%' }} />
                                </div>
                            )}
                            {!imagePreview && (
                                <div style={{ width: '300px', height: '300px', border: '1px solid #ccc', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
                                    <span>No Image</span>
                                </div>
                            )}
                        </label>
                        <div style={{ display: 'flex', flexDirection: 'column', width: '70%', height: '300px', border: '1px solid #ccc', marginBottom: '5px', marginRight: '5px', boxSizing: 'border-box' }}>
                            <div style={{ marginBottom: '2px', height: '20%' }}>
                                <h3 style={{ marginBottom: '2px' }}>Name:</h3>
                                <input type="text" value={name} onChange={(e) => setName(e.target.value)} style={{ width: '100%', height: '50%', boxSizing: 'border-box' }} />
                            </div>
                            <div style={{ marginBottom: '2px', height: '80%' }}>
                                <h3 style={{ marginBottom: '5px' }}>Description:</h3>
                                <textarea value={description} onChange={(e) => setDescription(e.target.value)} style={{ width: '100%', height: '80%', boxSizing: 'border-box' }} />
                            </div>
                        </div>
                    </div>

                    {/* product size */}
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', padding: '20px' }}>
                        {/* Table on the Left */}
                        <div style={{ flex: 1, marginRight: '20px' }}>
                            <table style={{ width: '100%', borderCollapse: 'collapse', border: '1px solid #ccc' }}>
                                <thead>
                                <tr style={{ backgroundColor: '#f2f2f2' }}>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Size</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Price</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Quantity</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Add</th>
                                    <th style={{ padding: '10px', textAlign: 'left' }}>Edit</th>
                                </tr>
                                </thead>
                                <tbody>
                                {/*{productDetails.map((size, index) => (*/}
                                {/*    <tr key={index} style={{ backgroundColor: 'white', border: '1px solid #ccc' }}>*/}
                                {/*        <td style={{ padding: '10px', textAlign: 'left' }}>{size.size}</td>*/}
                                {/*        <td style={{ padding: '10px', textAlign: 'left' }}>{size.price}</td>*/}
                                {/*        <td style={{ padding: '10px', textAlign: 'left' }}>{size.quantity}</td>*/}
                                {/*        <td style={{ padding: '10px', textAlign: 'left' }}><button>Add</button></td>*/}
                                {/*        <td style={{ padding: '10px', textAlign: 'left' }}><button>Edit</button></td>*/}
                                {/*    </tr>*/}
                                {/*))}*/}
                                </tbody>
                            </table>
                        </div>
                        {/* Buttons on the Right */}

                    </div>
                </div>
                {/* Right Section */}
                <div style={{ width: '20%', background: 'white', padding: '20px', boxSizing: 'border-box', border: '1px solid #ccc' }}>
                    {/* Design List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Design</h3>
                        <select style={{ width: '100%' }} value={design} onChange={(e) => setDesign(designMap[e.target.value])}>
                            {Design.map((design, index) => (
                                <option key={index}>{design}</option>
                            ))}
                        </select>
                    </div>
                    {/* Product Line List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Product Line</h3>
                        <select style={{ width: '100%' }} value={productLine} onChange={(e) => setProductLine(productLineMap[e.target.value])}>
                            {ProductLine.map((productLine, index) => (
                                <option key={index}>{productLine}</option>
                            ))}
                        </select>
                    </div>
                    {/* Material List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Material</h3>
                        <select style={{ width: '100%' }} value={material} onChange={(e) => setMaterial(materialMap[e.target.value])}>
                            {Material.map((material, index) => (
                                <option key={index}>{material}</option>
                            ))}
                        </select>
                    </div>
                    {/* Color List */}
                    <div style={{ marginBottom: '20px' }}>
                        <h3>Color</h3>
                        <select style={{ width: '100%' }} value={color} onChange={(e) => setColor(colorMap[e.target.value])}>
                            {Color.map((color, index) => (
                                <option key={index}>{color}</option>
                            ))}
                        </select>
                    </div>

                </div>

            </div>
        </div>
    );
};

export default ProductPage;
