import React from 'react';
import { useCart } from './CartContext';

const CartPage = () => {
    const { cartItems } = useCart();

    return (
        <div>
            <h2>Cart</h2>
            {cartItems.map((item, index) => (
                <div key={index}>
                    <p>{item.product.name}</p>
                    <p>Size: {item.size}  Quantity: {item.quantity}</p>
                </div>
            ))}
            {/* Checkout button */}
        </div>
    );
};

export default CartPage;
