import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, deleteSongById, getSongById } from '../../Api/SongApi';

const EditSong = () => {
    const { songId } = useParams();
    const [song, setSong] = useState({
        title: '',
        soundTrackFileName: '',
        soundTrackData: '',
        imageFileName: '',
        imageData: ''
        // Add other attributes here as needed
    });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSong = async () => {
            try {
                if (songId) {
                    const songData = await getSongById(songId);
                    setSong(songData);
                }
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId]);

    const handleChange = (event) => {
        const { name, value } = event.target;
        setSong((prevSong) => ({
            ...prevSong,
            [name]: value,
        }));
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            await updateSongById(songId, song);
            alert('Song updated successfully');
            navigate('/ListSong'); // Navigate back to the list of songs
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    const handleDelete = async () => {
        const confirmation = window.confirm('Are you sure you want to delete this song?');
        if (confirmation) {
            try {
                await deleteSongById(songId);
                alert('Song deleted successfully');
                navigate('/ListSong'); // Navigate back to the list of songs
            } catch (error) {
                console.error('Error deleting the song:', error);
            }
        }
    };

    return (
        <div>
            <h2>Edit Song</h2>
            {song && (
                <form onSubmit={handleSubmit}>
                    <div>
                        <label htmlFor="title">Title:</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            value={song.title}
                            onChange={handleChange}
                        />
                    </div>
                    <div>
                        <label htmlFor="soundTrack">Sound Track:</label>
                        {song.soundTrackData && (
                            <audio controls>
                                <source src={`data:audio/mpeg;base64,${song.soundTrackData}`} type="audio/mpeg" />
                                Your browser does not support the audio element.
                            </audio>
                        )}
                    </div>
                    <div>
                        <label htmlFor="image">Image:</label>
                        {song.imageData && (
                            <img src={`data:image/png;base64,${song.imageData}`} alt={`${song.title} cover art`} />
                        )}
                    </div>
                    <button type="submit">Update Song</button>
                    <button type="button" onClick={handleDelete}>Delete Song</button>
                </form>
            )}
        </div>
    );
};

export default EditSong;
