import React, {useEffect, useState} from 'react';
import { addNewSong } from '../../Api/SongApi';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import {storage} from "../../firebase"; // Import the additional functions

const AddPage = () => {
    // const db = app.firestore();

    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [genre, setGenre] = useState('');
    const [privacy, setPrivacy] = useState('public');
    const [isUploading, setIsUploading] = useState(false);

    const [soundtrackFileUrl, setSoundtrackFileUrl] = useState('');
    const [imageFileUrl, setImageFileUrl] = useState('');

    const navigate = useNavigate();
    const [fileUrl, setFileUrl] = React.useState(null);

    const genreToIntMap = {
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    };

    const handleTitleChange = (event) => setTitle(event.target.value);
    const handleGenreChange = (event) => setGenre(event.target.value); // Adjusted to use value directly from event
    const handlePrivacyChange = (event) => setPrivacy(event.target.value);
