import React, { useState } from 'react';
import './FavoritePage.css';
import favoriteFilled from '../Favorite/favorite-filled.svg';
import {useCart} from "../Cart/CartContext";

const FavoritePage = () => {
    const [favorites, setFavorites] = useState(JSON.parse(localStorage.getItem('favorites')) || []);
    const [selectedSize, setSelectedSize] = useState(""); // State for selected size
    const Size = ['35', '36', '36,5', '37', '38', '38,5', '39', '40', '40,5', '41', '42', '42,5', '43', '44', '44,5', '45', '46'];
    const [selectedQuantity, setSelectedQuantity] = useState(1);
    const toggleFavorite = (product) => {
        const index = favorites.findIndex((favProduct) => favProduct.productId === product.productId);
        if (index !== -1) {
            const updatedFavorites = [...favorites];
            updatedFavorites.splice(index, 1);
            setFavorites(updatedFavorites);
            localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
        }
    };

    return (
        <div>
            <h1 className="favorites-h1" >Favorites</h1>
            <div className="favorites-container">
                {favorites.map((product, index) => (
                    <div key={index} className="favorite-item">
                        {product.imageUrls && product.imageUrls.length > 0 && (
                            <img src={product.imageUrls[0]} alt={product.name} className="product-image" />
                        )}
                        <div className="product-info">
                            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <p>{product.name}</p>
                                <img src={favoriteFilled} onClick={() => toggleFavorite(product)} alt="Favorite" style={{ width: '30px', height: '30px', cursor: 'pointer',marginRight:'10px' }} />
                            </div>
                            <div style={{ display: 'flex', alignItems: 'center' }}>
                                <p>Price: {product.price} USD</p>
                            </div>
                        </div>

                    </div>
                ))}
            </div>
        </div>
    );
};

export default FavoritePage;
