import React, { useState } from 'react';
import { addNewSong } from '../../Api/SongApi';
import { useNavigate } from 'react-router-dom';

const SongPage = () => {
    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [genreId, setGenreId] = useState(null);
    const [releaseDate, setReleaseDate] = useState(new Date()); // Set to current date/time by default
    const [privacy, setPrivacy] = useState(false); // Default value for privacy
    const navigate = useNavigate(); // Get the navigate function

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
    };

    const handleSoundtrackChange = (event) => {
        const file = event.target.files[0];
        setSoundtrackFile(file);
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        setImageFile(file);
    };

    const handleGenreChange = (event) => {
        const selectedGenreId = event.target.value;
        setGenreId(selectedGenreId);
    };

    const handleReleaseDateChange = (event) => {
        const date = new Date(event.target.value);
        setReleaseDate(date);
    };

    const handlePrivacyChange = (event) => {
        const isChecked = event.target.checked;
        setPrivacy(isChecked);
    };

    const addSong = async (event) => {
        event.preventDefault();

        const user = JSON.parse(localStorage.getItem('user') || '{}'); // Parse the stored string to an object
        if (!user || !user.Id) {
            console.error('User ID is missing');
            // Handle missing user ID appropriately, e.g., show an error message
            return;
        }

        try {
            const formData = new FormData();
            formData.append('userId', user.Id); // Assuming the ID is stored under user.Id
            formData.append('title', title);
            formData.append('soundtrackFile', soundtrackFile);
            formData.append('imageFile', imageFile);
            formData.append('genreId', genreId);
            formData.append('releaseDate', releaseDate.toISOString()); // Convert to ISO string
            formData.append('privacy', privacy);

            await addNewSong(formData);
            navigate('/ListSong');
            window.location.reload();
        } catch (error) {
            console.error('Error:', error);
            // Optionally, display an error message to the user
        }
    };

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add New Song</h2>
                <form onSubmit={addSong}>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Title:</label>
                        <input type="text" value={title} onChange={handleTitleChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Soundtrack File:</label>
                        <input type="file" onChange={handleSoundtrackChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Image File:</label>
                        <input type="file" onChange={handleImageChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Genre:</label>
                        <select value={genreId} onChange={handleGenreChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="">Select Genre</option>
                            {/* Render genre options here */}
                        </select>
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Release Date:</label>
                        <input type="datetime-local" value={releaseDate.toISOString().slice(0, -8)} onChange={handleReleaseDateChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Privacy:</label>
                        <input type="checkbox" checked={privacy} onChange={handlePrivacyChange} style={{ marginRight: '5px' }} />
                        <span>Private</span>
                    </div>
                    <button type="submit" style={{ width: '100%', padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', cursor: 'pointer' }}>Add Song</button>
                </form>
            </div>
        </div>
    );

};

export default SongPage;


