import React from 'react';
import { useCart } from './CartContext';

const CartPage = () => {
    const { cartItems, removeFromCart } = useCart();
    let totalPrice = 0;

    const handleDeleteItem = (index) => {
        removeFromCart(index);
    };
    const handleCheckout = () => {
        navigate('/checkout'); // Redirect to the checkout page
    };


    return (
        <div style={{ display: 'flex', justifyContent: 'center',marginTop:'20px' }}>
            {/* Left section */}
            <div style={{ border: '1px solid #ccc'}}>
                <h2>Cart</h2>
                {cartItems.map((item, index) => {
                    const itemTotalPrice = item.product.price * item.quantity;
                    totalPrice += itemTotalPrice;
                    const formattedPrice = item.product.price.toLocaleString();
                    const formattedItemTotalPrice = itemTotalPrice.toLocaleString();

                    return (
                        <div key={index} style={{ display: 'flex', alignItems: 'center', padding: '5px', border: '1px solid #ccc', marginBottom: '20px' }}>
                            <div style={{ marginRight: '20px' }}>
                                <img src={item.product.imageUrls[0]} alt={item.product.name} style={{ maxWidth: '200px', maxHeight: '200px' }} />
                            </div>
                            <div>
                                <p>{item.product.name}</p>
                                <p>Price: {formattedPrice} VND</p>
                                <p>Size: {item.size}</p>
                                <p>Quantity: {item.quantity}</p>
                                <p>Total Price: {formattedItemTotalPrice} VND</p>
                                <button onClick={() => handleDeleteItem(index)}>Delete</button>
                            </div>
                        </div>
                    );
                })}
            </div>
            {/* Right section */}
            <div style={{ marginLeft: '50px', border: '1px solid #ccc' }}>
                <h2>Total Order Price</h2>
                <p>Total Price: {totalPrice.toLocaleString()} VND</p>
                <button onClick={() => console.log("Proceed to Checkout")}>Checkout</button>
            </div>
        </div>
    );
};

export default CartPage;
