import React, {useState, useEffect, useMemo} from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateSongById, getSongById } from '../../Api/SongApi';

const EditSong = () => {
    const { songId } = useParams();
    const navigate = useNavigate();
    const [song, setSong] = useState({
        title: '',
        imageFile: null,
        soundtrackFile: null,
        genre: '',
        privacy: 'public',
        releaseDate: '',
        imageFilePreview: '',
        soundtrackFilePreview: '',
    });

    const genreToIntMap= useMemo(()=>({
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    }),[]) ;

    const intToGenreMap = useMemo(() =>
            Object.keys(genreToIntMap).reduce((obj, key) => {
                obj[genreToIntMap[key]] = key;
                return obj;
            }, {}),
        [genreToIntMap]);

    useEffect(() => {
        // Declare fetchSong inside useEffect
        const fetchSong = async () => {
            try {
                const songData = await getSongById(songId);
                const { title, userId, nameOfGenre, releaseDate, privacy, soundTrackFileName, imageFileName,soundTrackData, } = songData;

                setSong({
                    title,
                    userId,
                    releaseDate,
                    genre: intToGenreMap[nameOfGenre],
                    privacy: privacy ? 'public' : 'private',
                    imageFilePreview: imageFileName.startsWith('http') ? imageFileName : `/${imageFileName}`,
                    soundtrackFilePreview: soundTrackFileName.startsWith('http') ? soundTrackFileName : `/${soundTrackFileName}`,
                });
            } catch (error) {
                console.error('Error fetching song:', error);
            }
        };

        fetchSong();
    }, [songId, intToGenreMap]);


    const handleFileChange = (event) => {
        const { name, files } = event.target;
        if (files.length > 0) {
            const fileUrl = URL.createObjectURL(files[0]);
            setSong({ ...song, [name]: files[0], [`${name}Preview`]: fileUrl });
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setSong({ ...song, [name]: value });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('title', song.title);
        formData.append('genreId', genreToIntMap[song.genre]);
        formData.append('privacy', song.privacy === "public");
        if (song.imageFile) {
            formData.append('imageFile', song.imageFile);
        }
        if (song.soundtrackFile) {
            formData.append('soundtrackFile', song.soundtrackFile);
        }

        try {
            await updateSongById(songId, formData);
            alert('Song updated successfully');
            navigate('/ListSong');
        } catch (error) {
            console.error('Error updating the song:', error);
        }
    };

    return (
        <div className="edit-song-container">
            <h2>Edit Song</h2>
            <form onSubmit={handleSubmit} className="edit-song-form">
                <div className="form-field">
                    <label htmlFor="title">Title:</label>
                    <input
                        id="title"
                        type="text"
                        name="title"
                        value={song.title}
                        onChange={handleChange}
                        placeholder="Enter song title"
                        className="text-input"
                    />
                </div>
                <div className="form-field">
                    <label htmlFor="imageFile">Image:</label>
                    <input
                        id="imageFile"
                        type="file"
                        name="imageFile"
                        onChange={handleFileChange}
                        className="file-input"
                    />
                    {song.imageFilePreview && (
                        <img src={song.imageFilePreview} alt="Song" className="preview-image" />
                    )}
                </div>
                <div className="form-field">
                    <label>Current Soundtrack:</label>
                    {song.soundtrackFilePreview && (
                        <audio controls className="audio-preview">
                            <source src={song.soundtrackFilePreview} type="audio/mpeg" />
                            Your browser does not support the audio element.
                        </audio>
                    )}
                </div>
                <div className="form-field">
                    <label htmlFor="soundtrackFile">Soundtrack:</label>
                    <input
                        id="soundtrackFile"
                        type="file"
                        name="soundtrackFile"
                        onChange={handleFileChange}
                        className="file-input"
                    />
                </div>
                <div className="form-field">
                    <label htmlFor="genre">Genre:</label>
                    <select
                        id="genre"
                        name="genre"
                        value={song.genre}
                        onChange={handleChange}
                        className="select-input"
                    >
                        {Object.entries(genreToIntMap).map(([genre, _]) => (
                            <option key={genre} value={genre}>{genre}</option>
                        ))}
                    </select>
                </div>
                <div className="form-field">
                    <label htmlFor="privacy">Privacy:</label>
                    <select
                        id="privacy"
                        name="privacy"
                        value={song.privacy}
                        onChange={handleChange}
                        className="select-input"
                    >
                        <option value="public">Public</option>
                        <option value="private">Private</option>
                    </select>
                </div>
                <div className="form-field">
                    <button type="submit" className="btn save-changes">Save Changes</button>
                </div>
            </form>
        </div>
    );
};

export default EditSong;
