import React, { useState } from "react";

const CustomDropdown = ({ options, onSelect, productSizes }) => {
    const [isOpen, setIsOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState("");

    // Helper function to map index to size value
    const getSizeValue = (index) => {
        const sizeIndex = options.indexOf(index.toString());
        return sizeIndex !== -1 ? options[sizeIndex] : null;
    };

    const handleSelect = (option) => {
        setSelectedOption(option);
        onSelect(option);
        setIsOpen(false);
    };

    return (
        <div style={{ position: 'relative' }}>
            <div
                onClick={() => setIsOpen(!isOpen)}
                style={{
                    padding: '8px',
                    border: '1px solid #ccc',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    minWidth: '100px',
                }}
            >
                {selectedOption || "Select"}
            </div>
            {isOpen && (
                <div
                    style={{
                        position: 'absolute',
                        top: '100%',
                        left: 0,
                        width: 'auto',
                        padding: '8px',
                        border: '1px solid #ccc',
                        borderRadius: '4px',
                        background: '#fff',
                        zIndex: 1,
                        display: 'flex',
                        flexWrap: 'wrap',
                        maxWidth: '300px',
                    }}
                >
                    {options.map((option, index) => {
                        const sizeValue = getSizeValue(option);
                        return (
                            <div
                                key={index}
                                onClick={() => handleSelect(option)}
                                style={{
                                    padding: '3px',
                                    cursor: 'pointer',
                                    width: '20%',
                                    justifyContent: 'center',
                                    display: 'flex',
                                    alignItems: 'center',
                                }}
                            >
                                {sizeValue || option}
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};

export default CustomDropdown;
