import React, { useState } from 'react';
import { searchByTitle } from "../Api/SongApi";

const SearchPage = ({ onSongSelect }) => {
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState({ songs: [], users: [], albums: [], playlists: [] });

    const handleSearch = async () => {
        if (!searchQuery) return; // Don't search if query is empty
        try {
            const results = await searchByTitle(searchQuery);
            setSearchResults(results);
        } catch (error) {
            console.error('Error searching:', error);
            setSearchResults({ songs: [], users: [], albums: [], playlists: [] }); // Reset/clear results on error
        }
    };

    const handleInputChange = (event) => {
        setSearchQuery(event.target.value);
    };
    const handleSongClick = (song) => {
        console.log("audio url: ",song.audioFileUrl)
        onSongSelect({
            audioSrc: song.audioFileUrl,
            imageSrc: song.imageFileUrl,
            title: song.title,
            artist: song.artist, // Assuming you have an artist property
        });
    };

    return (
        <div className="search-page-container">
            <h2>Search Songs</h2>
            <input
                type="text"
                value={searchQuery}
                onChange={handleInputChange}
                placeholder="Search by title..."
            />
            <button onClick={handleSearch}>Search</button>

            <div className="search-results">
                {searchResults.songs.length > 0 && (
                    <div>
                        <h3>Songs</h3>
                        <ul>
                            {searchResults.songs.map(song => (
                                <div key={song.songId} className="song-item">
                                    <img
                                        src={song.imageFileUrl}
                                        alt={`${song.title} cover art`}
                                        className="song-image"
                                        onClick={() => handleSongClick(song)}
                                    />
                                    <div className="song-details">
                                        <span className="song-title">{song.title}</span>
                                        <span className="song-info">{song.userName}</span>
                                    </div>
                                    <div className="song-details">
                                        <span className="song-info"> {song.nameOfAlbum}</span>
                                    </div>
                                    <div className="song-details">
                                        <span className="song-info"> {song.duration}</span>
                                    </div>
                                </div>
                            ))}
                        </ul>
                    </div>
                )}
                {searchResults.users.length > 0 && (
                    <div>
                        <h3>Artists</h3>
                        <ul>
                            {searchResults.users.map(user => (
                                <li key={user.id}>
                                    <span>{user.userName}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}

                {searchResults.albums.length > 0 && (
                    <div>
                        <h3>Albums</h3>
                        <ul>
                            {searchResults.albums.map(album => (
                                <li key={album.albumId}>
                                    <span>{album.nameOfAlbum}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
                {searchResults.playlists.length > 0 && (
                    <div>
                        <h3>Playlists</h3>
                        <ul>
                            {searchResults.playlists.map(playlist => (
                                <li key={playlist.playlistId}>
                                    <span>{playlist.title}</span>
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
            <style>
                {`
                    .song-item:hover {
                        background-color: #f0f0f0; /* Make it a little grayer on hover */
                    }
                    .song-item {
                        display: flex;
                        align-items: center;
                        margin-bottom: 10px;
                    }
                    .song-image {
                        width: 60px; 
                        height: 60px; 
                        margin-right: 10px;
                        border-radius: 50%;
                    }

                    .song-details {
                        flex: 1;
                    }

                    .song-title {
                        font-weight: bold;
                        font-size: 1.2rem; /* Adjust font size as needed */
                    }

                    .song-info {
                        color: #888;
                        font-size: 0.9rem; /* Adjust font size as needed */
                    }
                `}
            </style>
        </div>
    );
};

export default SearchPage;
