import axios from 'axios';

const baseLink = 'http://localhost:5163';

const authapi = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'application/json'
    }
});

authapi.interceptors.request.use((config) => {
    console.log("Interceptor hit for URL:", config.url); // Debugging
    const token = localStorage.getItem('token');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
}, (error) => {
    return Promise.reject(error);
});

const login = async (credentials) => {
    try {
        const response = await axios.post('http://localhost:5163/Account/login', credentials);
        localStorage.setItem('token', response.data.token);
        localStorage.setItem('user',response.data.user)
        localStorage.setItem('role',response.data.role)
        console.log('User logged in successfully.');
        return response.data; // Return the response data
    } catch (error) {
        console.error('Error:', error);
        throw error; // Throwing the error for further handling if necessary
    }
};

const register = async (userData) => {
    try {
        console.log("Register data", userData);
        const response = await authapi.post('/Account/register', userData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
export { login, register };
