import React, { useState, useEffect } from 'react';

const Account = () => {
    const [userData, setUserData] = useState({
        UserName: '',
        Email: '',
        Gender: ''
    });

    // Fetch user data from localStorage or API
    useEffect(() => {
        // Example: Fetch user data from an API
        const fetchUserData = async () => {
            try {
                const response = await fetch('/api/userdata'); // Adjust the API endpoint
                if (response.ok) {
                    const data = await response.json();
                    setUserData(data); // Set user data state
                } else {
                    console.error('Failed to fetch user data');
                }
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };

        fetchUserData(); // Call the fetchUserData function on component mount
    }, []);

    // Function to handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Example: Send updated user data to the server
            const response = await fetch('/api/updateuserdata', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(userData)
            });
            if (response.ok) {
                console.log('User data updated successfully');
            } else {
                console.error('Failed to update user data');
            }
        } catch (error) {
            console.error('Error updating user data:', error);
        }
    };

    // Function to handle input changes
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData(prevUserData => ({
            ...prevUserData,
            [name]: value
        }));
    };

    return (
        <div>
            <h2>Account Settings</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input type="text" name="UserName" value={userData.UserName} onChange={handleInputChange} />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="Email" value={userData.Email} readOnly />
                </div>
                <div>
                    <label>Gender:</label>
                    <select name="Gender" value={userData.Gender} onChange={handleInputChange}>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                        <option value="PreferNotToSay">Prefer not to say</option>
                        <option value="Other">Other</option>
                        <option value="NonBinary">Non-binary</option>
                    </select>
                </div>
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
};

export default Account;
