import React, { useState, useEffect, useRef } from "react";
import AudioControls from "./AudioControls";
import volumeIcon from '../Icon/icons8-volume-50.png';
import moreoption from '../Icon/icons8-more-50.png'
const AudioPlayer = ({ tracks }) => {
    const [trackIndex, setTrackIndex] = useState(0);
    const [trackProgress, setTrackProgress] = useState(0);
    const [isPlaying, setIsPlaying] = useState(false);
    const { title, artist, audioSrc,image } = tracks[trackIndex];
    const audioRef = useRef(new Audio(audioSrc));
    const intervalRef = useRef();
    const isReady = useRef(false);
    const [volume, setVolume] = useState(1.0);
    const [muted, setMuted] = useState(false);
    const { duration } = audioRef.current;

    const startTimer = () => {
        clearInterval(intervalRef.current);
        intervalRef.current = setInterval(() => {
            if (audioRef.current.ended) {
                toNextTrack();
            } else {
                setTrackProgress(audioRef.current.currentTime);
            }
        }, [1000]);
    };

    const onScrub = (value) => {
        clearInterval(intervalRef.current);
        audioRef.current.currentTime = value;
        setTrackProgress(audioRef.current.currentTime);
    };

    const onScrubEnd = () => {
        if (!isPlaying) {
            setIsPlaying(true);
        }
        startTimer();
    };

    const toPrevTrack = () => {
        if (trackIndex - 1 < 0) {
            setTrackIndex(tracks.length - 1);
        } else {
            setTrackIndex(trackIndex - 1);
        }
    };

    const toNextTrack = () => {
        if (trackIndex < tracks.length - 1) {
            setTrackIndex(trackIndex + 1);
        } else {
            setTrackIndex(0);
        }
    };
    const changeVolume = (newVolume) => {
        audioRef.current.volume = newVolume;
        setVolume(newVolume);
        setMuted(newVolume === 0);
    };

    const toggleMute = () => {
        setMuted(!muted);
        audioRef.current.muted = !muted;
        if (muted) {
            changeVolume(volume); // Restore the previous volume when unmuting
        }
    };

// Use this effect to update the volume when the state changes
    useEffect(() => {
        audioRef.current.volume = volume;
        setMuted(volume === 0);
    }, [volume]);

    useEffect(() => {
        if (isPlaying) {
            audioRef.current.play();
            startTimer();
        } else {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    useEffect(() => {
        audioRef.current.pause();

        audioRef.current = new Audio(audioSrc);
        setTrackProgress(audioRef.current.currentTime);

        if (isReady.current) {
            audioRef.current.play();
            setIsPlaying(true);
            startTimer();
        } else {
            isReady.current = true;
        }
    }, [trackIndex]);

    useEffect(() => {
        return () => {
            audioRef.current.pause();
            clearInterval(intervalRef.current);
        };
    }, []);

    const playerContainerStyle = {display: 'flex', justifyContent: 'space-between', alignItems: 'center', backgroundColor: '#000000',
        color: '#ffffff', position: 'fixed', bottom: '0', left: '0', right: '0', width: '100%', maxWidth: '1920px', margin: '0 auto',};

    const leftSectionStyle = {display: 'flex', alignItems: 'center', justifyContent: 'flex-start',};

    const centerSectionStyle = {display: 'flex', flexDirection: 'column', alignItems: 'center', flex: 1,};

    const rightSectionStyle = {display: 'flex', alignItems: 'center', justifyContent: 'space-between', width: '150px'};

    const progressBarStyle = {width: '100%',};

    const volumeIconPath = volume === 0 ? '../Icon/icons8-mute-50.png' :
        (volume < 0.5 ? '../Icon/icons8-low-volume-50.png' :
            (volume < 1 ? '../Icon/icons8-volume-50.png' : '../Icon/icons8-volume-up-50.png'));


    return (
        <div className="audio-player" style={playerContainerStyle}>
            <div style={leftSectionStyle}>
                <img
                    src={tracks[trackIndex].image}
                    alt={`Track artwork for ${tracks[trackIndex].title} by ${tracks[trackIndex].artist}`}
                    style={{ width: '90px', height: '90px', borderRadius: '90px',margin:'1px' }}
                />
                <div >
                    <h2 >{tracks[trackIndex].title}</h2>
                    <p >{tracks[trackIndex].artist}</p>
                </div>

            </div>

            <div style={centerSectionStyle}>
                <AudioControls
                    isPlaying={isPlaying}
                    onPrevClick={toPrevTrack}
                    onNextClick={toNextTrack}
                    onPlayPauseClick={setIsPlaying}
                />
                <input
                    type="range"
                    value={trackProgress}
                    step="1"
                    min="0"
                    max={duration || 0}
                    className="progress"
                    onChange={(e) => onScrub(e.target.value)}
                    onMouseUp={onScrubEnd}
                    onKeyUp={onScrubEnd}
                    style={progressBarStyle}
                />
            </div>

            <div style={rightSectionStyle}>
                <img src={muted ? '../Icon/icons8-mute-50.png' : volumeIconPath} alt="Volume control" onClick={toggleMute}
                    style={{ cursor: 'pointer', marginRight: '1rem' }}
                />
                <input type="range" min="0" max="1" step="0.01" value={muted ? 0 : volume}
                    onChange={(e) => changeVolume(parseFloat(e.target.value))}
                    style={{ marginRight: '1rem' }}
                />
                <img src={moreoption} alt="More options" />
            </div>
        </div>
    );
};

export default AudioPlayer;
