import authApi from './AxiosConfig';

// Function to add a new order
const addOrder = async (orderInfo) => {
    try {
        const response = await authApi.post(`/Order`, orderInfo);
        console.log('OrderId: ', response.data);
        return response.data;
    } catch (error) {
        console.error('Error adding order:', error);
        throw error;
    }
};

// Function to create a PayPal payment
const createPayPalPayment = async (total, currency = 'USD') => {
    try {
        const response = await authApi.post(`/Payment/create`, { total, currency });
        console.log('PayPal Payment URL:', response.data);
        return response.data;
    } catch (error) {
        console.error('Error creating PayPal payment:', error);
        throw error;
    }
};

export { addOrder, createPayPalPayment };
