import React, { useEffect, useRef, useState } from 'react';
import AudioControls from './AudioControls'; // Update the import path as necessary

const AudioPlayer = ({ track, onPlayPauseClick }) => {
    const audioRef = useRef(new Audio());
    const [isReady, setIsReady] = useState(false);

    // Load new track when the 'track' prop changes
    useEffect(() => {
        if (track && track.audioSrc) {
            // Set the source of the audio element to the audio stream URL
            audioRef.current.src = track.audioSrc;
            audioRef.current.load();
            setIsReady(true); // Set the flag to true indicating that the audio is ready to be played
        }
    }, [track]);

    // Handle play/pause from external controls
    useEffect(() => {
        if (isReady) { // Only play if the audio is ready
            onPlayPauseClick ? audioRef.current.play() : audioRef.current.pause();
        }
    }, [onPlayPauseClick, isReady]);

    if (!track) {
        return null; // Do not render if there is no track
    }

    return (
        <footer style={{ width: '100%', position: 'fixed', bottom: 0, backgroundColor: '#fff', boxShadow: '0 -2px 5px rgba(0, 0, 0, 0.2)' }}>
            <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '1rem' }}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    {/* Render track image and details */}
                    <img src={track.image} alt={track.title} style={{ width: '50px', height: '50px', marginRight: '1rem' }} />
                    <div>
                        <h3 style={{ marginBottom: '0.5rem' }}>{track.title}</h3>
                        <p style={{ margin: 0 }}>{track.artist}</p>
                    </div>
                </div>
                {/* Render audio controls */}
                <AudioControls
                    isPlaying={onPlayPauseClick}
                    onPlayPauseClick={() => onPlayPauseClick(!onPlayPauseClick)}
                />
            </div>
            {/* Hidden audio element */}
            <audio ref={audioRef} onEnded={() => onPlayPauseClick(false)} />
        </footer>
    );
};

export default AudioPlayer;
