import axios from 'axios';

const baseLink = 'http://localhost:5163';

const authApi = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'application/json'
    }
});

// authApi.interceptors.request.use((config) => {
//     console.log("Interceptor hit for URL:", config.url); // Debugging
//     const token = localStorage.getItem('accessToken');
//     if (token) {
//         config.headers.Authorization = `Bearer ${token}`;
//     }
//     return config;
// }, (error) => {
//     return Promise.reject(error);
// });
authApi.interceptors.response.use(
    (response) => response, // On success, just return the response
    async (error) => {
        const originalRequest = error.config;
        if (error.response.status === 401 && !originalRequest._retry) {
            originalRequest._retry = true; // Mark this request as already tried
            const refreshToken = localStorage.getItem('refreshToken');
            console.log('refresh token get from local storage: ',refreshToken)
            try {
                const tokenResponse = await authApi.post('/Account/refresh-token', { Token: refreshToken });
                if (tokenResponse.data && tokenResponse.data) {
                    console.log('New access token received: ', tokenResponse.data.accessToken);
                    localStorage.setItem('accessToken', tokenResponse.data.accessToken);
                    originalRequest.headers['Authorization'] = `Bearer ${tokenResponse.data.accessToken}`;
                    return authApi(originalRequest); // Retry the original request with the new token
                }
            } catch (refreshError) {
                console.error('Error refreshing token: ', refreshError);
                return Promise.reject(refreshError);
            }
        }
        return Promise.reject(error);
    }
);




const login = async (credentials) => {
    try {
        const response = await authApi.post('/Account/login', credentials);
        localStorage.setItem('accessToken', response.data.accessToken);
        localStorage.setItem('refreshToken', response.data.refreshToken);
        localStorage.setItem('user',response.data.user)
        localStorage.setItem('role',response.data.role)
        console.log('User logged in successfully.');
        return response.data; // Return the response data
    } catch (error) {
        console.error('Error:', error);
        throw error; // Throwing the error for further handling if necessary
    }
};

const register = async (userData) => {
    try {
        console.log("Register data", userData);
        const response = await authApi.post('/Account/register', userData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const getAuthorizedMessage = async () => {
    try {
        const response = await authApi.get('/User/authorized');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const getUserOnlyMessage = async () => {
    try {
        const response = await authApi.get('/User/user-only');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const getAdminAuthorizedMessage = async () => {
    try {
        const response = await authApi.get('/Admin/authorized');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const getAdminOnlyMessage = async () => {
    try {
        const response = await authApi.get('/Admin/admin-only');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
export { login, register,getAuthorizedMessage,getUserOnlyMessage };
