// ProductSizeApi.js
import authApi from './AxiosConfig'; // Import the configured Axios instance

const addProductSize = async (productSizeData) => {
    try {
        const response = await authApi.post(`/ProductSize`, productSizeData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const getProductSizesByProductId = async (productId) => {
    try {
        const response = await authApi.get(`/ProductSize/${productId}`);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

export { addProductSize, getProductSizesByProductId };
