import React, {useEffect, useState} from 'react';
import { addNewSong } from '../../Api/SongApi';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import {storage} from "../../firebase"; // Import the additional functions

const AddPage = () => {
    // const db = app.firestore();

    const [title, setTitle] = useState('');
    const [soundtrackFile, setSoundtrackFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);
    const [genre, setGenre] = useState('');
    const [privacy, setPrivacy] = useState('public');
    const [isUploading, setIsUploading] = useState(false);
    const [soundtrackUrl, setSoundtrackUrl] = useState('');
    const [imageUrl, setImageUrl] = useState('');
    const navigate = useNavigate();
    const [fileUrl, setFileUrl] = React.useState(null);

    const genreToIntMap = {
        Workout: 0,
        Relaxation: 1,
        Party: 2,
        StudyFocus: 3,
        MoodBooster: 4,
        Chill: 5,
        Travel: 6,
        Morning: 7,
        Nighttime: 8,
        Throwback: 9,
        LoveSongs: 10,
        SadSongs: 11,
        Jazz: 12,
        Classical: 13,
        Indie: 14,
        Rock: 15,
        Pop: 16,
        HipHopRap: 17,
        Electronic: 18,
        RandBSoul: 19,
    };

    const handleTitleChange = (event) => setTitle(event.target.value);
    const handleGenreChange = (event) => setGenre(event.target.value); // Adjusted to use value directly from event
    const handlePrivacyChange = (event) => setPrivacy(event.target.value);
    // const handleSoundtrackChange = (event) => setSoundtrackFile(event.target.files[0]);
    // const handleImageChange = (event) => setImageFile(event.target.files[0]);
    const handleAudioFileUploadChange = async (e) => {
        const files = e.target.files;
        if (files.length === 0) {
            return;
        }
        const file = files[0];
        const url = await uploadAudioFile(file);
        setFileUrl(url);
    };
    const handleImageFileUploadChange = async (e) => {
        const files = e.target.files;
        if (files.length === 0) {
            return;
        }
        const file = files[0];
        const url = await uploadImageFile(file);
        setFileUrl(url);
    };
    const uploadAudioFile = async (file) => {
        if (!file) return null;
        try {
            const fileRef = ref(storage, `audio/${file.name}`);
            await uploadBytes(fileRef, file);
            const downloadURL = await getDownloadURL(fileRef);
            console.log('Uploaded file:', file.name);
            console.log('Download URL:', downloadURL);
            setSoundtrackUrl(downloadURL)
            return downloadURL;
        } catch (error) {
            console.error('Error uploading file:', error);
            return null;
        }
    };
    const uploadImageFile = async (file) => {
        if (!file) return null;
        try {
            const fileRef = ref(storage, `image/${file.name}`);
            await uploadBytes(fileRef, file);
            const downloadURL = await getDownloadURL(fileRef);
            console.log('Uploaded file:', file.name);
            console.log('Download URL:', downloadURL);
            setImageUrl(downloadURL);
            return downloadURL;
        } catch (error) {
            console.error('Error uploading file:', error);
            return null;
        }
    };
    const addSong = async (event) => {
        event.preventDefault();
        const userRole = localStorage.getItem('role');
        if (userRole !== 'Artist') {
            alert('You must be an Artist to upload songs.');
            return;
        }
        setIsUploading(true);
        try {
            const newSoundtrackUrl = await uploadAudioFile(soundtrackFile);
            console.log("New soundtrackUrl:", newSoundtrackUrl);
            setSoundtrackUrl(newSoundtrackUrl); // Sets the new URL in state

            const newImageUrl = await uploadImageFile(imageFile);
            console.log("New imageUrl:", newImageUrl);
            setImageUrl(newImageUrl); // Sets the new URL in state

            console.log("soundtrackUrl:", newSoundtrackUrl);
            console.log("imageUrl:", newImageUrl);
        } catch (error) {
            console.error('Failed to upload files or add the new song:', error);
        } finally {
            setIsUploading(false);
        }
    };


    const genreOptions = Object.keys(genreToIntMap).map(genreName => (
        <option key={genreName} value={genreName}>{genreName}</option>
    ));

    return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ width: '400px', padding: '20px', border: '1px solid #ccc', borderRadius: '5px', boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)' }}>
                <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Add New Song</h2>
                <form onSubmit={addSong}>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Title:</label>
                        <input type="text" value={title} onChange={handleTitleChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Soundtrack File:</label>
                        <input type="file" onChange={handleAudioFileUploadChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Image File:</label>
                        <input type="file" onChange={handleImageFileUploadChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }} />
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Genre:</label>
                        <select value={genre} onChange={handleGenreChange} required style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="">Select Genre</option>
                            {genreOptions}
                        </select>
                    </div>
                    <div style={{ marginBottom: '15px' }}>
                        <label>Privacy:</label>
                        <select value={privacy} onChange={handlePrivacyChange} style={{ width: '100%', padding: '8px', borderRadius: '5px', border: '1px solid #ccc' }}>
                            <option value="public">Public</option>
                            <option value="private">Private</option>
                        </select>
                    </div>
                    <button type="submit" style={{ width: '100%', padding: '10px', borderRadius: '5px', backgroundColor: '#007bff', color: '#fff', border: 'none', cursor: 'pointer' }}>Add Song</button>
                </form>
            </div>
        </div>
    );
};

export default AddPage;
