import React, { useState, useEffect, useRef } from 'react';
import AudioControls from './AudioControls'; // Adjust the import path as necessary

const AudioPlayer = ({ track }) => {
    const audioRef = useRef(new Audio());
    const [isPlaying, setIsPlaying] = useState(false);

    // Effect hook to update audio source and manage playback
    useEffect(() => {
        if (track && track.audioSrc) {
            audioRef.current.src = track.audioSrc; // Set the audio source
            audioRef.current.load(); // Load the audio stream
        }
    }, [track]);

    useEffect(() => {
        if (isPlaying) {
            audioRef.current.play().catch(error => console.error('Error playing audio:', error));
        } else {
            audioRef.current.pause();
        }
    }, [isPlaying]);

    // Play/pause handler
    const handlePlayPause = () => {
        setIsPlaying(!isPlaying);
    };

    // Basic audio controls
    const renderAudioControls = () => (
        <AudioControls
            isPlaying={isPlaying}
            onPlayPauseClick={handlePlayPause}
        />
    );

    // Render
    return (
        <div>
            {track ? (
                <>
                    <div>
                        <h3>{track.title}</h3>
                        <p>{track.artist}</p>
                    </div>
                    {renderAudioControls()}
                </>
            ) : (
                <p>No track selected</p>
            )}
        </div>
    );
};

export default AudioPlayer;
