import axios from 'axios';

const baseLink = 'http://localhost:5163';

const songApi = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'multipart/form-data'
    }
});
const authApi = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'application/json'
    }
});

let refreshingTokenPromise = null;
songApi.interceptors.response.use(
    (response) => response,
    async (error) => {
        const originalRequest = error.config;
        if (error.response.status === 401 && !originalRequest._retry) {
            if (!refreshingTokenPromise) {
                originalRequest._retry = true;
                refreshingTokenPromise = authApi.post('/Account/refresh-token', { Token: localStorage.getItem('refreshToken') })
                    .then(tokenResponse => {
                        if (tokenResponse.data && tokenResponse.data.accessToken) {
                            localStorage.setItem('accessToken', tokenResponse.data.accessToken);
                            console.log('New access token received: ', tokenResponse.data.accessToken);
                            songApi.defaults.headers['Authorization'] = `Bearer ${tokenResponse.data.accessToken}`;
                            originalRequest.headers['Authorization'] = `Bearer ${tokenResponse.data.accessToken}`;
                            return songApi(originalRequest);
                        }
                    }).finally(() => {
                        refreshingTokenPromise = null;
                    });
            }
            return refreshingTokenPromise;
        }
        return Promise.reject(error);
    }
);
const getSongById = async (id) => {
    try {
        const response = await songApi.get(`/Song/${id}`);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};


const deleteSongById = async (id) => {
    try {
        const response = await songApi.delete(`/Song/${id}`);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const getAllSongsByUserId = async (userId) => {
    try {
        const response = await songApi.get(`/Song/user/${userId}`);
        const songs = response.data;

        const pageSize = 10; // Number of songs per page
        const paginatedSongs = [];
        for (let i = 0; i < songs.length; i += pageSize) {
            paginatedSongs.push(songs.slice(i, i + pageSize));
        }
        console.log("get song by id ",songs)

        return paginatedSongs;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
}

const getAllSongs = async () => {
    try {
        const response = await songApi.get('/Song');
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

// Function to search songs by title
const searchByTitle = async (title) => {
    try {
        const response = await songApi.get('/Search/search', {
            params: { title }
        });
        console.log("search by title: ",response.data)
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const addNewSong = async (songData) => {
    try {
        const response = await songApi.post('/Song', songData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const updateSongById = async (id, updatedSongData) => {
    try {
        const response = await songApi.put(`/Song/${id}`, updatedSongData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const streamSongById = async (songId) => {
    try {
        const response = await songApi.get(`http://localhost:5163/Song/stream/${songId}`,{
            responseType: 'blob'
        });
        return response.data;
    } catch (error) {
        console.error('Error streaming song:', error);
        throw error;
    }
};
