import axios from 'axios';

const baseLink = 'http://localhost:5163';

const authApi = axios.create({
    baseURL: baseLink,
    headers: {
        'Content-Type': 'application/json'
    }
});

authApi.interceptors.request.use((config) => {
    console.log("Interceptor hit for URL:", config.url); // Debugging
    const token = localStorage.getItem('accessToken');
    if (token) {
        config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
    }, (error) => {
        return Promise.reject(error);
        },async (error) => {
        if (error.response) {
            await checkAndRefreshToken(error.response);
            const originalRequest = error.config;
            const newAccessToken = localStorage.getItem('accessToken');
            if (newAccessToken && error.response.status === 401 && !originalRequest._retry) {
                originalRequest._retry = true;
                originalRequest.headers.Authorization = `Bearer ${newAccessToken}`;
                return authApi(originalRequest);
            }
        }
    return Promise.reject(error);
});

function parseAuthHeader(header) {
    if (header.startsWith('Bearer')) {
        const parts = header.split(',');
        const errorParts = parts.map(part => part.trim().split('='));
        const errorDescription = errorParts.find(part => part[0] === 'error_description');
        if (errorDescription && errorDescription[1]) {
            // Assuming the error description is wrapped in quotes
            return errorDescription[1].slice(1, -1);
        }
    }
    return null;
}

async function checkAndRefreshToken(apiResponse) {
    if (apiResponse.status === 401) {
        const authHeader = apiResponse.headers.get('Www-Authenticate');
        const errorMessage = parseAuthHeader(authHeader);

        if (errorMessage && errorMessage.includes('expired')) {
            const refreshToken = localStorage.getItem('refreshToken');
            if (refreshToken) {
                const refreshResponse = await authApi.post('/Account/refresh-token', {
                    Token: refreshToken
                });

                if (refreshResponse.status === 200) {
                    const data = refreshResponse.data;
                    localStorage.setItem('accessToken', data);
                } else {
                }
            }
        }
    }
}


const login = async (credentials) => {
    try {
        const response = await axios.post('http://localhost:5163/Account/login', credentials);
        localStorage.setItem('accessToken', response.data.accessToken);
        localStorage.setItem('refreshToken', response.data.refreshToken);
        localStorage.setItem('user',response.data.user)
        localStorage.setItem('role',response.data.role)
        console.log('User logged in successfully.');
        return response.data; // Return the response data
    } catch (error) {
        console.error('Error:', error);
        throw error; // Throwing the error for further handling if necessary
    }
};

const register = async (userData) => {
    try {
        console.log("Register data", userData);
        const response = await authApi.post('/Account/register', userData);
        return response.data;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
const getAuthorizedMessage = async () => {
    try {
        const response = await authApi.get('/User/authorized');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};

const getUserOnlyMessage = async () => {
    try {
        const response = await authApi.get('/User/user-only');
        return response.data.message;
    } catch (error) {
        console.error('Error:', error);
        throw error;
    }
};
export { login, register,getAuthorizedMessage,getUserOnlyMessage };
