import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { ref, uploadBytes, getDownloadURL } from "firebase/storage";
import { storage } from "../../firebase";
import defaultAlbumImage from '../../Img/default-cover-art.png';
import { getAllSongsByUserId } from '../../Api/SongApi';
import { addNewAlbum } from '../../Api/ArtistApi';

const AddAlbumPage = () => {
    const [albumName, setAlbumName] = useState('');
    const [albumImage, setAlbumImage] = useState(null);
    const [imagePreview, setImagePreview] = useState(defaultAlbumImage);
    const [selectedSongs, setSelectedSongs] = useState(new Set());
    const [allUserSongs, setAllUserSongs] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        loadUserSongs();
    }, []);

    const loadUserSongs = async () => {
        try {
            const userSongs = await getAllSongsByUserId(); // Assuming it returns an array of user's songs
            setAllUserSongs(userSongs);
        } catch (error) {
            console.error('Error loading user songs:', error);
        }
    };

    const handleImageChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setAlbumImage(file);
            const previewUrl = URL.createObjectURL(file);
            setImagePreview(previewUrl);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (!albumName || !albumImage) {
                alert('Please provide album name and image.');
                return;
            }

            const formData = new FormData();
            formData.append('NameOfAlbum', albumName);

            const imageFileRef = ref(storage, `AlbumImages/${albumImage.name}`);
            await uploadBytes(imageFileRef, albumImage);
            const imageURL = await getDownloadURL(imageFileRef);
            formData.append('ImageAlbumUrl', imageURL);

            // Include logic for adding songs to the album here if needed

            const result = await addNewAlbum(formData);
            console.log(result);
            alert('Album added successfully!');
            navigate('/ListAlbum');
        } catch (error) {
            console.error('Error adding album:', error);
            alert('Failed to add album.');
        }
    };

    const handleSelectSong = (songId) => {
        const newSelectedSongs = new Set(selectedSongs);
        if (newSelectedSongs.has(songId)) {
            newSelectedSongs.delete(songId);
        } else {
            newSelectedSongs.add(songId);
        }
        setSelectedSongs(newSelectedSongs);
    };

    return (
        <div>
            <h2>Add New Album</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="albumName">Album Name *</label>
                    <input
                        id="albumName"
                        type="text"
                        value={albumName}
                        onChange={(e) => setAlbumName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="albumImage">Album Image *</label>
                    <input
                        id="albumImage"
                        type="file"
                        accept="image/*"
                        onChange={handleImageChange}
                        required
                    />
                </div>
                {imagePreview && (
                    <div>
                        <h3>Image Preview</h3>
                        <img src={imagePreview} alt="Album Preview" style={{ maxWidth: '200px' }} />
                    </div>
                )}
                <button type="submit">Add Album</button>
            </form>

            {/* Button to add songs */}
            <div>
                <h3>Add Songs to Album</h3>
                {allUserSongs.map((song) => (
                    <div key={song.songId}>
                        <input
                            type="checkbox"
                            checked={selectedSongs.has(song.songId)}
                            onChange={() => handleSelectSong(song.songId)}
                        />
                        {song.title}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default AddAlbumPage;
